AND C.AS_NO = #{asNum}
AND C.AS_TYPE_ID IN
#{item}
AND C.AS_STUS_ID IN
#{item}
AND F.AS_RESULT_NO = #{resultNum}
AND B.SALES_ORD_NO =#{orderNum}
AND A.NAME = #{custName}
AND A.NRIC = #{nricNum}
AND C.AS_REQST_DT >= TO_DATE(#{createStrDate}, 'DD/MM/YYYY')
AND C.AS_REQST_DT <= TO_DATE(#{createEndDate}, 'DD/MM/YYYY')
AND C.AS_REQST_DT between TO_DATE(#{createStrDate}, 'DD/MM/YYYY') and TO_DATE(#{createEndDate}, 'DD/MM/YYYY')
