                          , SALES_ORDER_NO
                          , STUS
                          , CUST_NAME
                          , PRODUCT_CODE
                          , PRODUCT_NAME
                          , APP_TYPE
                          , SALES_PROMOTION
                          , CONTRACT_DURATION
                          , OUTSTANDING
                          , SIRIM_NO
                          , SERIAL_NO
                          , MEMBERSHIP_CONTRACT_EXPIRY
                          , DSC_CODE
                          , PROD_CAT
                          , REG_ID
                          , DEFECT_CODE
                          , DEFECT_DESC
                          , CRT_USER_ID
                          , CRT_DT
                          , UPD_USER_ID
                          , UPD_DT
                          , REMARK

    ) VALUES ( SVC0130D_ID_SEQ.NEXTVAL
                  ,#{salesOrderNo}
                  ,#{stus}
                  ,#{custName}
                  ,#{productCode}
                  ,#{productName}
                  ,#{appType}
                  ,#{salesPromotion}
                  ,#{contractDuration}
                  ,#{outstanding}
                  ,#{sirimNo}
                  ,#{serialNo}
                  ,#{membershipContractExpiry}
                  ,#{dscCode}
                  ,#{prodCat}
                  ,#{regId}
                  ,#{defectCode}
                  ,#{defectDesc}
                  ,#{crtUserId}
                  ,SYSDATE
                  ,#{updUserId}
                  ,SYSDATE
                  ,#{remark}
    )

mapper.select:
SELECT A.REG_ID AS USER_ID, A.SALES_ORDER_NO AS SALES_ORDER_NO, A.PROD_CAT AS PROD_CAT, C.CODE AS STUS, TO_CHAR (A.CRT_DT, 'YYYY-MM-DD') AS CRT_DT, A.APPV_REMARK AS REMARK FROM SVC0130D A JOIN SYS0047M B on A.REG_ID = B.USER_NAME JOIN SYS0038M C on C.STUS_CODE_ID = A.STUS WHERE 1 = 1
AND B.USER_ID = #{userId}
AND TO_CHAR (A.CRT_DT, 'YYYY-MM-DD') >= TO_CHAR(TO_DATE (#{fromDate}, 'YYYYMMDD'),'YYYY-MM-DD')
AND TO_CHAR (A.CRT_DT, 'YYYY-MM-DD') <= TO_CHAR(TO_DATE (#{toDate}, 'YYYYMMDD'),'YYYY-MM-DD')
AND A.SALES_ORDER_NO = #{salesOrderNo}
AND A.STUS = #{stus}
ORDER BY A.CRT_DT DESC
