mapper.select:
SELECT A.CPS_NO AS CPS_NO, A.CUST_ID AS CUS_ID, D.NAME AS CUS_NM, B.SALES_ORD_NO AS ORD_NO, A.AS_NO AS AS_NO, A.ASR_NO AS ASR_NO, NVL(TO_CHAR(A.AS_RQST_DT, 'DD/MM/YYYY'), '-') AS AS_RQST_DT, NVL(TO_CHAR(A.CRT_DT, 'DD/MM/YYYY'), '-') AS CRT_DT, NVL(TO_CHAR(A.COMP_DT, 'DD/MM/YYYY'), '-') AS COMP_DT, NVL(TO_CHAR(A.ISSUE_DT, 'DD/MM/YYYY'), '-') AS ISSUE_DT, F.CODE || '-' || F.NAME AS BRCH_CDE, E.STK_CODE || ' - ' || E.STK_DESC AS STK_DESC, A.CSP_ITM_PRT_ID AS CSP_ITM_PRT_ID, K.CODE_DESC AS CSP_PFT_TYP_ID, J.CODE_DESC AS CSP_MLFNC_RSN_ID, H.CODE_DESC AS CSP_TYP_ID, I.CODE_DESC AS CSP_RESP_ID, L.CODE_DESC AS DEPT_TO_BEAR, TO_CHAR(NVL(A.CSP_AMT, 0), '9,999,999,999,999.99') AS CSP_AMT, G.CODE_NAME AS STUS_CODE_ID, A.STUS_CODE_ID AS STUS_ID, A.RCD_TMS AS RCD_TMS FROM SVC0053M A JOIN SAL0001D B ON A.SALES_ORD_ID = B.SALES_ORD_ID JOIN SAL0002D C ON B.SALES_ORD_ID = C.SALES_ORD_ID JOIN SAL0029D D ON B.CUST_ID = D.CUST_ID AND D.STUS_CODE_ID = 1 JOIN SYS0026M E ON C.ITM_STK_ID = E.STK_ID LEFT JOIN SYS0005M F ON A.BRCH_CDE = F.BRNCH_ID JOIN SYS0094M G ON A.STUS_CODE_ID = G.CODE AND G.IND = 'CPS_STA' LEFT JOIN SYS0013M H ON A.CSP_TYP_ID = H.CODE_ID AND H.CODE_MASTER_ID = '409' LEFT JOIN SYS0013M I ON A.CSP_SLUTN_RSN_ID = I.CODE_ID AND I.CODE_MASTER_ID = '410' LEFT JOIN SYS0013M J ON A.CSP_MLFNC_RSN_ID = J.CODE_ID AND J.CODE_MASTER_ID = '411' LEFT JOIN SYS0013M K ON A.CSP_DFT_TYP_ID = K.CODE_ID AND K.CODE_MASTER_ID = '412' LEFT JOIN SYS0013M L ON A.DEPT_TO_BEAR = L.CODE_ID AND L.CODE_MASTER_ID = '359' WHERE 1=1
AND B.SALES_ORD_NO = #{orderNum}
AND A.CUST_ID = #{customerCode}
AND UPPER(D.NAME) = UPPER(#{customerNm})
AND C.ITM_STK_ID = #{prodId}
AND A.ISSUE_DT >= TO_DATE(#{applicationStrDate} || ' 00:00:00', 'DD/MM/YYYY HH24:MI:SS')
AND A.ISSUE_DT <= TO_DATE(#{applicationEndDate} || ' 23:59:59', 'DD/MM/YYYY HH24:MI:SS')
AND A.COMP_DT >= TO_DATE(#{compStrDate} || ' 00:00:00', 'DD/MM/YYYY HH24:MI:SS')
AND A.COMP_DT <= TO_DATE(#{compEndDate} || ' 23:59:59', 'DD/MM/YYYY HH24:MI:SS')
AND A.BRCH_CDE = #{brchCde}
AND A.CSP_TYP_ID = #{cpsTyp}
AND A.DEPT_TO_BEAR = #{respTyp}
AND A.CSP_MLFNC_RSN_ID = #{cause}
AND A.CSP_DFT_TYP_ID = #{evtTyp}
AND A.STUS_CODE_ID = #{status}
ORDER BY A.CPS_NO DESC

SELECT T.LOG_ID , T.SALES_ORD_ID , T.PRGRS_ID , T.LOG_DT , T.REF_ID , T.IS_LOK , T.LOG_CRT_USER_ID , T.LOG_CRT_DT , T.PRGRS , T.USER_NAME FROM ( SELECT T1.LOG_ID , T1.SALES_ORD_ID , T1.PRGRS_ID , T1.LOG_DT , T1.REF_ID , T1.IS_LOK , T1.LOG_CRT_USER_ID , T1.LOG_CRT_DT , T2.PRGRS , DECODE ( T3.USER_ID, NULL, '', T3.USER_NAME) AS USER_NAME FROM SAL0009D T1 JOIN SAL0010M T2 ON T2.PRGRS_ID = T1.PRGRS_ID LEFT JOIN SYS0047M T3 ON T3.USER_ID = T1.LOG_CRT_USER_ID WHERE T1.SALES_ORD_ID = #{salesOrderId}
AND T2.PRGRS_ID = #{prgrsId}
ORDER BY T1.LOG_ID DESC ) T WHERE ROWNUM <= 1

mapper.update:
UPDATE SVC0053M SET AS_NO = #{asNoHid}
                  , ASR_NO = #{asrNoHid}
                  , STUS_CODE_ID = #{stusCodeId}
                  , AS_RQST_DT = TO_DATE(#{asRqstDt}, 'yyyymmdd')
                  , COMP_DT = TO_DATE(#{compDt}, 'yyyymmdd')
                  , ISSUE_DT = TO_DATE(#{issueDt}, 'yyyymmdd')
                  , DEPT_TO_BEAR  = #{inChrDept}
                  , BRCH_CDE = #{branchCde}
                  , CSP_ITM_PRT_ID = #{dfctPrt}
                  , CSP_DFT_TYP_ID = #{evtTyp}
                  , CSP_MLFNC_RSN_ID = #{cause}
                  , CSP_SLUTN_RSN_ID = #{solutionMtd}
                  , CSP_TYP_ID = #{cpsTyp}
                  , CSP_ITM = #{cspItm}
                  , CSP_ITM_OTH = #{searchdepartment}
                  , CSP_RMK = #{rmk}
                  , CSP_AMT = #{compTotAmt}
                  , ATCH_FILE_GRP_ID = NVL(#{atchFileGrpId}, ATCH_FILE_GRP_ID)
                  , UPD_USER_ID = #{updator}
                  , UPD_DT = SYSDATE
 WHERE CPS_NO =  #{cpsNo}

mapper.select:
SELECT COUNT(CPS_NO) AS C
FROM SVC0053M
WHERE SALES_ORD_ID = #{ordId}
  AND AS_NO = #{asNoHid}
  AND ASR_NO = #{asrNoHid}
  AND STUS_CODE_ID IN ('1', '44')
