mapper.select:
SELECT SOM.SALES_ORD_ID, SOM.SALES_ORD_NO, TO_CHAR(TO_DATE(SOM.SALES_DT),'DD/MM/YYYY') SALES_DT, SOM.APP_TYPE_ID, CD.CODE APP_TYPE, C.NAME CUST_NM, C.NRIC, S.CODE as ORD_STUS, V2.INST_AREA, V2.INST_STATE, TO_CHAR(TO_DATE(V2.FIRST_INSTALL_DT,'DD/MM/YYYY'),'DD/MM/YYYY') FIRST_INSTALL_DT, CG.SRV_CODY_ID, MEM.MEM_CODE, MEM.NAME MEM_NM, MEM.STUS MEM_STUS, CG.SRV_BS_GEN, CG.SRV_CONFIG_ID FROM SAL0001D SOM JOIN SYS0013M CD ON CD.CODE_ID = SOM.APP_TYPE_ID JOIN SAL0029D C ON C.CUST_ID = SOM.CUST_ID JOIN SYS0038M S ON S.STUS_CODE_ID = SOM.STUS_CODE_ID JOIN SAL1010V_V2 V2 ON V2.ORD_ID = SOM.SALES_ORD_ID JOIN SAL0090D CG ON CG.SRV_SO_ID = SOM.SALES_ORD_ID AND CG.SRV_STUS_ID = 1 LEFT JOIN ORG0001D MEM ON MEM.MEM_ID = CG.SRV_CODY_ID WHERE 1 = 1
AND SOM.SALES_ORD_NO= #{salesOrdNo}
AND SOM.APP_TYPE_ID = #{appType}
AND UPPER(V2.INST_STATE) = UPPER(#{instState})
AND C.NAME = #{CustNm}
AND C.NRIC = #{NRIC}
AND UPPER(V2.INST_AREA) LIKE UPPER(#{instArea})
AND TO_CHAR(TO_DATE(V2.FIRST_INSTALL_DT,'DD/MM/YYYY'),'MM/YYYY') = #{instMonth}
AND CG.SRV_BS_GEN = 1
AND (CG.SRV_CODY_ID = 0 OR CG.SRV_CODY_ID = 3 OR CG.SRV_CODY_ID = 51)
AND MEM.MEM_CODE = #{inchargeMem}
ORDER BY SALES_ORD_NO
