SELECT distinct Project2.No No , Project2.HS_DATE HS_DATE , Project2.STUS_CODE_ID STUS_CODE_ID , Project2.Code Code , Project2.SALES_ORD_NO SALES_ORD_NO , Project2.ASSIGN_CODY C5 , Project2.ACTION_CODY as ACTN_MEM_ID, Project2.MEM_ID , Project2.Name Name , Project2.cust_id, Project2.cody_status_nm, Project2.BRNCH_ID as BRNCH_ID, Project2.SCHDUL_ID SCHDUL_ID , Project2.SALES_ORD_ID, Project2.branch_cd, Project2.CODY_MANGR_USER_ID, Project2.Result as Result from( SELECT HS.NO AS NO, HS.STUS_CODE_ID as STUS_CODE_ID, HS.MONTH || '/' || HS.YEAR as HS_DATE , HS.MONTH, HS.YEAR, ST.CODE, HR.NO AS Result, SO.SALES_ORD_NO, AP.CODE AS AppType, CU.NAME, CD.MEM_CODE AS ASSIGN_CODY, CD.MEM_ID, AC.MEM_CODE AS ACTION_CODY, MG.STATE, MG.AREA, cu.CUST_ID as cust_id, (select code from SYS0038M where 1=1 and stus_code_id = CD.STUS ) as cody_status_nm, SO.BRNCH_ID as BRNCH_ID, HS.SCHDUL_ID SCHDUL_ID , SO.SALES_ORD_ID, BR.CODE as branch_cd, MG.CODY_MANGR_USER_ID FROM SVC0008D HS INNER JOIN sal0001d SO ON SO.sales_ord_id = HS. sales_ord_id INNER JOIN SAL0045D INS ON SO.SALES_ORD_ID = HS.SALES_ORD_ID INNER JOIN sal0023d AD ON AD.cust_add_id = INS.ADD_ID AND AD.CUST_ID = SO.CUST_ID INNER JOIN sys0064m MG ON MG.area_id = AD.area_id LEFT JOIN svc0006d HR ON HR.schdul_id = HS.schdul_id INNER JOIN sys0038m ST ON ST.stus_code_id = HS.stus_code_id INNER JOIN sys0013m AP ON AP.code_id = SO.app_type_id INNER JOIN sal0029d CU ON CU.cust_id = SO.cust_id INNER JOIN org0001d CD ON CD.mem_id = HS.cody_id LEFT JOIN org0001d AC ON AC.mem_id = HS. actn_mem_id LEFT JOIN sys0005m BR ON BR.code = MG.cody_brnch_code LEFT JOIN org0005d OG ON OG.dept_code = MG.cody_mangr_user_id WHERE HS.YEAR = TO_CHAR(to_date(#{myBSMonth} ,'MM/YYYY'),'YYYY') AND HS.MONTH = TO_CHAR(to_date(#{myBSMonth},'MM/YYYY'),'MM')
AND HS.CODY_ID = #{cmdcodyCode}
and SO.PV_MONTH = TO_CHAR(to_date(#{myInstallMonth},'MM/YYYY'),'MM')
AND SO.PV_YEAR = TO_CHAR(to_date(#{myInstallMonth} ,'MM/YYYY'),'YYYY')
AND HS.NO = #{txtHsOrderNo}
AND ( SO.SALES_ORD_NO = #{txtSalesOrder})
AND HS.STUS_CODE_ID = #{cmbStatusType}
AND SO.CUST_ID = #{txtCustomer}
AND ( br.BRNCH_id = #{cmdBranchCode})
AND OG.MEM_ID = #{cmdCdManager}
and rownum < 500 )Project2

