SELECT Project2.No No , Project2.HS_DATE HS_DATE , Project2.STUS_CODE_ID STUS_CODE_ID , Project2.Code Code , Project2.SALES_ORD_NO SALES_ORD_NO , Project2.ASSIGN_CODY C5 , Project2.ACTION_CODY as ACTN_MEM_ID, Project2.MEM_ID , Project2.Name Name , Project2.cust_id, Project2.cody_status_nm, Project2.BRNCH_ID as BRNCH_ID, Project2.SCHDUL_ID SCHDUL_ID , Project2.SALES_ORD_ID, Project2.branch_cd, Project2.CODY_MANGR_USER_ID from( SELECT HS.NO AS NO, HS.STUS_CODE_ID as STUS_CODE_ID, HS.MONTH || '/' || HS.YEAR as HS_DATE , HS.MONTH, HS.YEAR, ST.CODE, HR.NO AS Result, SO.SALES_ORD_NO, AP.CODE AS AppType, CU.NAME, CD.MEM_CODE AS ASSIGN_CODY, CD.MEM_ID, AC.MEM_CODE AS ACTION_CODY, MG.STATE, MG.AREA, cu.CUST_ID as cust_id, (select code from SYS0038M where 1=1 and stus_code_id = cu.STUS_CODE_ID ) as cody_status_nm, SO.BRNCH_ID as BRNCH_ID, HS.SCHDUL_ID SCHDUL_ID , SO.SALES_ORD_ID, BR.CODE as branch_cd, MG.CODY_MANGR_USER_ID FROM SVC0008D HS INNER JOIN SAL0001D SO ON SO.SALES_ORD_ID = HS.SALES_ORD_ID INNER JOIN SAL0023D AD ON AD.CUST_ADD_ID = SO.CUST_ADD_ID INNER JOIN SYS0064M MG ON MG.AREA_ID = AD.AREA_ID left JOIN SVC0006D HR ON HR.SCHDUL_ID = HS.SCHDUL_ID INNER JOIN SYS0038M ST ON ST.STUS_CODE_ID = HS.STUS_CODE_ID INNER JOIN SYS0013M AP ON AP.CODE_ID = SO.APP_TYPE_ID INNER JOIN SAL0029D CU ON CU.CUST_ID = SO.CUST_ID INNER JOIN ORG0001D CD ON CD.MEM_ID = HS.CODY_ID left JOIN ORG0001D AC ON AC.MEM_ID = HS.ACTN_MEM_ID
LEFT JOIN SYS0005M BR ON BR.CODE = MG.CODY_BRNCH_CODE LEFT JOIN ORG0005D OG ON OG.DEPT_CODE = MG.CODY_MANGR_USER_ID WHERE HS.YEAR = TO_CHAR(to_date(#{myBSMonth} ,'MM/YYYY'),'YYYY') AND HS.MONTH = TO_CHAR(to_date(#{myBSMonth},'MM/YYYY'),'MM')
AND HS.CODY_ID = #{cmdcodyCode}
and SO.PV_MONTH = TO_CHAR(to_date(#{myInstallMonth},'MM/YYYY'),'MM')
AND SO.PV_YEAR = TO_CHAR(to_date(#{myInstallMonth} ,'MM/YYYY'),'YYYY')
AND HS.NO = #{txtHsOrderNo}
AND ( SO.SALES_ORD_NO = #{txtSalesOrder})
AND HS.STUS_CODE_ID = #{cmbStatusType}
AND SO.CUST_ID = #{txtCustomer}
AND ( br.BRNCH_id = #{cmdBranchCode})
AND OG.MEM_ID = #{cmdCdManager}
and rownum < 500 )Project2

