mapper.select:
select a.BS_RATE_ROW_ID, CASE WHEN l.MEM_LVL <> 1 THEN '' ELSE A.ORG_CODE END AS ORG_CODE, CASE WHEN ( CASE WHEN ( l.MEM_LVL <> 1 AND l.MEM_LVL <> 2 ) THEN '' ELSE a.GRP_CODE END ) = '0' THEN '' ELSE ( CASE WHEN ( l.MEM_LVL <> 1 AND l.MEM_LVL <> 2 ) THEN '' ELSE a.GRP_CODE END) END AS GRP_CODE, CASE WHEN ( CASE WHEN l.MEM_LVL = 4 THEN '' ELSE a.DEPT_CODE END ) = '0' THEN '' ELSE ( CASE WHEN l.MEM_LVL = 4 THEN '' ELSE a.DEPT_CODE END ) END AS DEPT_CODE, CASE WHEN a.MEM_CODE = '0' then '' else a.MEM_CODE END AS MEM_CODE, a.TOT_BS, a.ACT, a.FAIL, a.CANCL, a.CMPLT, a.CMPLT_OWN, a.CMPLT_OTR, a.BS_TRGET_WEEK1, a.BS_SUCCES_CNT_WEEK1, a.BS_SUCCES_RATE_WEEK1, a.BS_SUCCES_BALNC_WEEK1, a.BS_TRGET_WEEK2, a.BS_SUCCES_CNT_WEEK2, a.BS_SUCCES_RATE_WEEK2, a.BS_SUCCES_BALNC_WEEK2, a.BS_TRGET_WEEK3, a.BS_SUCCES_CNT_WEEK3, a.BS_SUCCES_RATE_WEEK3, a.BS_SUCCES_BALNC_WEEK3, a.BS_TRGET_WEEK4, a.BS_SUCCES_CNT_WEEK4, a.BS_SUCCES_RATE_WEEK4, a.BS_SUCCES_BALNC_WEEK4
from REP0008S a join ( select c.MEM_LVL, c.LAST_ORG_CODE from SYS0047M a inner join ORG0001D b on b.MEM_CODE = a.USER_NAME inner join ORG0005D c on c.MEM_ID = b.MEM_ID
where a.USER_ID = #{userId}
where c.MEM_LVL = '1'
) l on l.LAST_ORG_CODE = a.ORG_CODE where 1=1 AND ORG_CODE = #{orgCode}
AND GRP_CODE = UPPER(#{grpCode})
AND DEPT_CODE = #{deptCode}
AND MEM_CODE = #{memCode}
ORDER BY BS_RATE_ROW_ID
