mapper.select:
SELECT
HCM.TXN_SURVYE_CODE AS CODY_CODE, HCM.TXN_SURVYE_NAME AS CODY_NAME, ORG.DEPT_CODE AS DEPT_CODE, ORG.GRP_CODE AS GRP_CODE, ORG.ORG_CODE AS ORG_CODE, CCRM.TOTAL_POINT AS TOTAL_MARK,
TRUNC(NVL(CCRM.PCNT, 0), 2) || '%' AS PCNT, CCRM.CNT FROM CBT0004M HCM
JOIN CBT0001M HCC ON HCC.CTRL_ID = HCM.CTRL_ID
LEFT JOIN ORG1001V ORG ON ORG.MEM_CODE = HCM.TXN_SURVYE_CODE JOIN ORG0001D ORG1 ON ORG.MEM_ID = ORG1.MEM_ID LEFT JOIN ( SELECT --C.TXN_TYPE_ID AS CCR_TXN_TYPE_ID, C.TXN_SURVYE_CODE AS CCR_TXN_SURVYE_CODE, --D.CCR_HC_ID, SUM(D.TOTAL_POINT) AS TOTAL_POINT, SUM(D.CNT) AS CNT, SUM(D.MAX_MARK) AS MAX_MARK, ((SUM(D.TOTAL_POINT) / SUM((D.MAX_MARK))) * 100) AS PCNT FROM CBT0004M C LEFT JOIN ( SELECT A.HC_ID CCR_HC_ID, SUM(A.HC_ITM_POINT) TOTAL_POINT, COUNT(A.HC_ID) CNT, SUM(E.MAX_POINT) MAX_MARK FROM CCR0002D A JOIN CBT0004M B ON A.HC_ID = B.HC_ID JOIN CBT0002M E ON E.SURV_ID = A.HC_DEF_ID WHERE 1=1
AND b.txn_dt >= TO_DATE('01/' || #{periodMonth}, 'DD/MM/YYYY') AND b.txn_dt <= last_day(TO_DATE('01/' || #{periodMonth}, 'DD/MM/YYYY'))
GROUP BY A.HC_ID ) D ON C.HC_ID = D.CCR_HC_ID WHERE 1=1 AND CCR_HC_ID IS NOT NULL AND c.txn_dt >= TO_DATE('01/' || #{periodMonth}, 'DD/MM/YYYY') AND c.txn_dt <= last_day(TO_DATE('01/' || #{periodMonth}, 'DD/MM/YYYY')) GROUP BY C.TXN_SURVYE_CODE ) CCRM ON CCRM.CCR_TXN_SURVYE_CODE = HCM.TXN_SURVYE_CODE WHERE 1=1
AND CCRM.CNT IS NOT NULL AND ORG1.MEM_TYPE = #{memTyp}
AND HCM.TXN_SURVYE_CODE = #{agentCode}
AND hcm.txn_dt >= TO_DATE('01/' || #{periodMonth}, 'DD/MM/YYYY') AND hcm.txn_dt <= last_day(TO_DATE('01/' || #{periodMonth}, 'DD/MM/YYYY'))
AND ORG.DEPT_CODE = #{deptCode}
AND ORG.GRP_CODE = #{grpCode}
AND ORG.ORG_CODE = #{orgCode}
AND ORG1.IS_AC = #{isAc}
GROUP BY HCM.TXN_SURVYE_CODE, ORG.DEPT_CODE, ORG.GRP_CODE, ORG.ORG_CODE, HCM.TXN_SURVYE_NAME, CCRM.TOTAL_POINT, CCRM.PCNT, CCRM.CNT
ORDER BY HCM.TXN_SURVYE_CODE

SELECT SAL.SALES_ORD_ID AS SALES_ORD_ID, SAL.SALES_ORD_NO AS SALES_ORD_NO, SAL.REF_NO AS REF_NO, HCM.TXN_CUST_NAME AS CUST_NAME, DES2.CODE_DESC AS APP_TYPE_DESC, SAL.APP_TYPE_ID AS APP_TYPE_ID, HCC.CTRL_TYPE AS HC_TYPE_CODE, DES.CODE_DESC AS HC_TYPE_DESC, CUST.TYPE_ID AS CUST_TYPE_CODE, DES1.CODE_DESC AS CUST_TYPE_DESC, SALD.ITM_STK_ID AS STOCK_ID, STK.STK_CODE AS STOCK_CODE, STK.STK_DESC AS STOCK_DESC, HCM.TXN_SURVYE_CODE AS MEM_CODE, ORG1.FULL_NAME AS MEM_NAME, ORG.DEPT_CODE AS DEPT_CODE, ORG.GRP_CODE AS GRP_CODE, ORG.ORG_CODE AS ORG_CODE, TO_CHAR(HCR.STATISTICS_SENT, 'DD/MM/YYYY') AS STAT_SEND_DT, ((CCR.POINTS / CCR.MAX_POINTS) * 100) AS SATISFACTION, HCM.HC_ID AS HC_ID, CCR.POINTS AS POINTS, CCR.MAX_POINTS AS MAX_POINTS, RATE.RATINGS, COMM.ANSW AS COMM FROM CBT0004M HCM JOIN CBT0005D HCR ON HCR.TXN_ID = HCM.TXN_ID JOIN CBT0001M HCC ON HCC.CTRL_ID = HCM.CTRL_ID LEFT JOIN SYS0013M DES ON DES.CODE_ID = HCC.CTRL_TYPE LEFT JOIN ORG1001V ORG ON ORG.MEM_CODE = HCM.TXN_SURVYE_CODE JOIN SAL0001D SAL ON SAL.SALES_ORD_ID = HCM.TXN_ORD_ID JOIN SAL0002D SALD ON SALD.SALES_ORD_ID = SAL.SALES_ORD_ID JOIN SYS0026M STK ON STK.STK_ID = SALD.ITM_STK_ID JOIN SAL0029D CUST ON CUST.CUST_ID = SAL.CUST_ID LEFT JOIN SYS0013M DES1 ON DES1.CODE_ID = CUST.TYPE_ID JOIN ORG0001D ORG1 ON ORG.MEM_ID = ORG1.MEM_ID LEFT JOIN SYS0013M DES2 ON DES2.CODE_ID = SAL.APP_TYPE_ID AND DES2.CODE_MASTER_ID = 10 LEFT JOIN (SELECT A.HC_ID, SUM(A.HC_ITM_POINT) POINTS, SUM(E.MAX_POINT) MAX_POINTS FROM CCR0002D A JOIN CBT0002M E ON E.SURV_ID = A.HC_DEF_ID GROUP BY A.HC_ID) CCR ON CCR.HC_ID = HCM.HC_ID LEFT JOIN ( SELECT HC_ID, QUES_ID, ANSW FROM ( SELECT TXN_ID, HC_ID, CAST(REGEXP_SUBSTR(COLUMN_VALUE, '[^":"]+', 1, 1) AS NUMBER DEFAULT 0 ON CONVERSION ERROR) AS QUES_ID, REGEXP_SUBSTR(COLUMN_VALUE, '[^":"]+', 1, 2) AS ANSW FROM (SELECT AA.TXN_ID, BA.HC_ID, REGEXP_REPLACE(AA.RESPONSE, '"{|}"', '"') X FROM CBT0005D AA JOIN CBT0004M BA ON AA.TXN_ID = BA.TXN_ID WHERE 1=1 AND AA.RESPONSE IS NOT NULL ) A,
LATERAL( SELECT TRIM(regexp_substr(REPLACE(X,'","','$'),'[^$]+', 1, level)) COLUMN_VALUE FROM DUAL connect BY regexp_substr(REPLACE(X,'","','$'), '[^$]+', 1, level) is not null ) B ) T JOIN ( SELECT DISTINCT SURV_ID, TMPL_TYPE FROM CBT0003D WHERE TMPL_TYPE IN ('7362') ) QA ON QA.SURV_ID = T.QUES_ID ) COMM ON COMM.HC_ID = HCM.HC_ID LEFT JOIN ( SELECT TXN_ID, REPLACE(REPLACE(REGEXP_REPLACE(SUBSTR(RATINGS, 1, INSTR(RATINGS , ',', -1)-1), '\d*:',''),'yes', '5'),'no', '1') AS RATINGS FROM( SELECT TXN_ID, SUBSTR(REGEXP_REPLACE(RATINGS, '"', ''), 1, INSTR(REGEXP_REPLACE(RATINGS, '"', '') , ':', -1)-1) AS RATINGS FROM ( SELECT TXN_ID as TXN_ID, REGEXP_REPLACE(RESPONSE, '"{|}"', '"') AS RATINGS FROM CBT0005D ) ) ) RATE ON RATE.TXN_ID = HCR.TXN_ID WHERE 1=1 AND HCM.TXN_STUS_ID = 121 AND HCR.RESPONSE IS NOT NULL
AND ORG1.MEM_TYPE = #{memTyp}
AND HCM.TXN_SURVYE_CODE = #{selectedMemCode}
AND TO_CHAR(HCR.STATISTICS_SENT, 'MM/YYYY') = #{periodMonth}
AND ORG.DEPT_CODE = #{selectedDeptCode}
AND ORG.GRP_CODE = #{selectedGrpCode}
AND ORG.ORG_CODE = #{selectedOrgCode}
