mapper.select:
SELECT CODE_ID, CODE_NAME FROM SYS0013M
           WHERE CODE_MASTER_ID = #{groupCode}
           AND DISAB = 0

mapper.insert:
/* APPV_PRCSS_STUS : R - Request, P - Approve In Progress, A - Approved, J - Rejected */
    INSERT INTO SVC0119M
    (
        APPV_PRCSS_NO
        ,APPV_REQ_NO
        ,REQST_DT
        ,REQST_USER_ID
        ,APPV_LINE_CNT
        ,APPV_LINE_PRCSS_CNT
        ,APPV_PRCSS_DESC
        ,CRT_DT
        ,CRT_USER_ID
        ,UPD_DT
        ,UPD_USER_ID
    )
    values
    (
        #{appvPrcssNo}
        ,#{cpeReqId}
        ,SYSDATE
        ,#{userName}
        ,#{appvLineCnt}
        ,0
        ,#{appvPrcssDesc}
        ,SYSDATE
        ,#{userId}
        ,SYSDATE
        ,#{userId}
        )

INSERT INTO SVC0121D
(
    REQ_DTL_ID
   ,CPE_REQ_ID
   ,MAIN_DEPT
   ,SUB_DEPT
   ,REMARK
   ,STUS_CODE_ID
   ,CRT_DT
   ,CRT_USER_ID
)
values
(
   SVC0121D_SEQ.NEXTVAL
   ,#{cpeReqId}
   ,#{mainDept}
   ,#{subDept}
   ,#{inputRemark}
   ,#{status}
   ,SYSDATE
   ,#{userId}
)

mapper.update:
UPDATE SVC0118M
    SET APPV_PRCSS_NO = #{appvPrcssNo}
    ,UPD_DT = SYSDATE
    ,UPD_USER_ID = #{userId}
    ,APPV_REQRD = 1
WHERE CPE_REQ_ID = #{cpeReqId}

mapper.select:
SELECT T1.SALES_ORD_NO ,T1.SALES_ORD_ID ,T1.CPE_REQ_ID ,TO_CHAR(T1.CRT_DT, 'YYYY-MM-DD') CRT_DT ,T1.CPE_SUBTYPE ,T1.AS_NO ,T3.NAME AS CUSTOMER_NAME ,T3.NRIC AS NRIC_COMPANY_NO ,T4.CODE_NAME AS MAIN_DEPT ,T5.CODE_NAME AS SUB_DEPT ,T6.CODE || ' - ' || T6.NAME AS DSC_BRANCH ,T10.NAME AS REQUEST_STAGE ,T8.CODE || ' - ' || T8.NAME AS REQUESTOR_BRANCH ,T9.NAME AS STATUS FROM SVC0118M T1 JOIN SAL0001D T2 ON T2.SALES_ORD_ID = T1.SALES_ORD_ID JOIN SAL0029D T3 ON T3.CUST_ID = T1.CUST_ID JOIN SYS0013M T4 ON T4.CODE = T1.REQ_MAIN_DEPT JOIN SYS0013M T5 ON T5.CODE = T1.REQ_SUB_DEPT JOIN SYS0047M T7 ON T7.USER_ID = T1.CRT_USER_ID JOIN SYS0005M T8 ON T8.BRNCH_ID = T7.USER_BRNCH_ID JOIN SYS0038M T9 ON T9.STUS_CODE_ID = T1.CPE_STUS_ID JOIN SYS0038M T10 ON T10.STUS_CODE_ID = T1.REQ_STAGE_ID JOIN SAL0045D T11 ON T11.SALES_ORD_ID = T1.SALES_ORD_ID JOIN SYS0005M T6 ON T6.BRNCH_ID = T11.BRNCH_ID WHERE 1=1
AND T1.CPE_REQ_ID = #{cpeReqId}
AND T1.SALES_ORD_NO = #{order_no}
AND T1.AS_NO = #{asNo}
AND UPPER(T3.NAME) LIKE '%'|| UPPER(#{customer_name}) ||'%'
AND T1.REQ_STAGE_ID IN
#{item}
AND UPPER(T3.NRIC) LIKE '%'||UPPER(#{nric_company_no})||'%'
AND T1.CRT_DT >= TO_DATE(#{reqStartDt}||' 00:00:00', 'DD/MM/YYYY HH24:MI:SS')
AND T1.CRT_DT <= TO_DATE(#{reqEndDt} ||' 23:59:59', 'DD-MM-YYYY HH24:MI:SS')
AND T1.REQUESTOR_BRANCH IN
#{item}
AND T4.CODE = #{main_department}
AND T5.CODE = #{sub_department}
AND T1.CPE_STUS_ID IN
#{item}
AND T11.BRNCH_ID IN
#{item}
ORDER BY T1.CRT_DT DESC

mapper.update:
UPDATE SVC0118M
    SET CPE_STUS_ID = #{status}
    ,UPD_DT = SYSDATE
    ,UPD_USER_ID = #{userId}
WHERE CPE_REQ_ID = #{cpeReqId}
