    (
        #{appvPrcssNo}
        ,#{cpeReqId}
        ,SYSDATE
        ,#{userName}
        ,#{appvLineCnt}
        ,0
        ,#{appvPrcssDesc}
        ,SYSDATE
        ,#{userId}
        ,SYSDATE
        ,#{userId}
        )

