mapper.update:
UPDATE SVC0118M
    SET APPV_PRCSS_NO = #{appvPrcssNo}
    ,UPD_DT = SYSDATE
    ,UPD_USER_ID = #{userId}
    ,APPV_REQRD = 1
WHERE CPE_REQ_ID = #{cpeReqId}

mapper.select:
SELECT * FROM ( SELECT T2.DEPT_CODE ,T2.ORG_CODE ,T2.GRP_CODE ,T1.SALES_ORD_NO ,T1.SALES_ORD_ID ,T1.CPE_REQ_ID ,TO_CHAR(T1.CRT_DT, 'YYYY-MM-DD') CRT_DT ,T1.CPE_SUBTYPE ,T1.AS_NO ,T3.NAME AS CUSTOMER_NAME ,T3.NRIC AS NRIC_COMPANY_NO ,T4.CODE_NAME AS MAIN_DEPT ,T5.CODE_NAME AS SUB_DEPT ,T6.CODE || ' - ' || T6.NAME AS DSC_BRANCH ,T10.NAME AS REQUEST_STAGE ,T8.CODE || ' - ' || T8.NAME AS REQUESTOR_BRANCH ,T9.NAME AS STATUS FROM SVC0118M T1 LEFT JOIN SAL0001D T2 ON T2.SALES_ORD_ID = T1.SALES_ORD_ID LEFT JOIN SAL0029D T3 ON T3.CUST_ID = T1.CUST_ID LEFT JOIN SYS0013M T4 ON T4.CODE = T1.REQ_MAIN_DEPT LEFT JOIN SYS0013M T5 ON T5.CODE = T1.REQ_SUB_DEPT LEFT JOIN SYS0047M T7 ON T7.USER_ID = T1.CRT_USER_ID LEFT JOIN SYS0005M T8 ON T8.BRNCH_ID = T7.USER_BRNCH_ID LEFT JOIN SYS0038M T9 ON T9.STUS_CODE_ID = T1.CPE_STUS_ID LEFT JOIN SYS0038M T10 ON T10.STUS_CODE_ID = T1.REQ_STAGE_ID LEFT JOIN SAL0045D T11 ON T11.SALES_ORD_ID = T1.SALES_ORD_ID LEFT JOIN SYS0005M T6 ON T6.BRNCH_ID = T11.BRNCH_ID WHERE 1=1
AND T1.CPE_REQ_ID = #{cpeReqId}
AND T1.SALES_ORD_NO = #{order_no}
AND T1.AS_NO = #{asNo}
AND UPPER(T3.NAME) LIKE '%'|| UPPER(#{customer_name}) ||'%'
AND T1.REQ_STAGE_ID IN
#{item}
AND UPPER(T3.NRIC) LIKE '%'||UPPER(#{nric_company_no})||'%'
AND T1.CRT_DT >= TO_DATE(#{reqStartDt}||' 00:00:00', 'DD/MM/YYYY HH24:MI:SS')
AND T1.CRT_DT <= TO_DATE(#{reqEndDt} ||' 23:59:59', 'DD-MM-YYYY HH24:MI:SS')
AND T1.REQUESTOR_BRANCH IN
#{item}
AND T1.CPE_STUS_ID IN
#{item}
AND T11.BRNCH_ID IN
#{item}
AND T1.CPE_TYPE IN
#{item}
ORDER BY T1.CRT_DT DESC ) A WHERE 1=1
AND (A.DEPT_CODE = #{deptCode})
AND (A.GRP_CODE = #{grpCode})
AND (A.ORG_CODE = #{orgCode})

mapper.update:
UPDATE SVC0118M
    SET CPE_STUS_ID = #{status}
    ,UPD_DT = SYSDATE
    ,UPD_USER_ID = #{userId}
WHERE CPE_REQ_ID = #{cpeReqId}

UPDATE SVC0148M
    SET BRNCH_ID = #{dscBrnchId}
    ,UPD_DT = SYSDATE
    ,UPD_USER_ID = #{userId}
WHERE ECPE_REQ_ID = #{cpeReqId}

UPDATE SVC0148M
    SET ECPE_STUS_ID = '5'
    ,APPROVAL_REMARK = #{apprvResn}
    ,UPD_DT = SYSDATE
    ,UPD_USER_ID = #{userId}
WHERE ECPE_REQ_ID = #{cpeReqId}

UPDATE SVC0148M
    SET ECPE_STUS_ID = '6'
    ,APPROVAL_REMARK = #{rejctResn}
    ,UPD_DT = SYSDATE
    ,UPD_USER_ID = #{userId}
WHERE ECPE_REQ_ID = #{cpeReqId}

UPDATE SAL0001D
     SET CUST_CNT_ID = #{custCntcId}
   
     ,CUST_ADD_ID = #{custAddId}
   
    ,UPD_DT = SYSDATE
    ,UPD_USER_ID = #{userId}
WHERE SALES_ORD_ID = #{salesOrdId}

UPDATE SAL0045D
     SET  ADD_ID = #{custAddId}
     ,CNT_ID = #{custCntcId}
    ,UPD_DT = SYSDATE
    ,UPD_USER_ID = #{userId}
WHERE SALES_ORD_ID = #{salesOrdId}

UPDATE SAL0024D
   SET CUST_BILL_UPD_USER_ID = #{userId}
     , CUST_BILL_UPD_DT      = SYSDATE
   
     , CUST_BILL_ADD_ID      = #{custAddId}
   
   
     , CUST_BILL_CNT_ID      = #{custCntcId}
   
 WHERE CUST_BILL_ID = (SELECT CUST_BILL_ID FROM SAL0001D WHERE SALES_ORD_ID = #{salesOrdId})

mapper.select:
SELECT
    T.AREA_ID,
    E.BRNCH_ID
FROM SYS0064M T
LEFT JOIN SYS0005M E ON E.CODE = T.CT_BRNCH_CODE AND E.TYPE_ID = 43
WHERE T.STATE = #{statValue}
AND T.CITY = #{cityValue}
AND T.POSTCODE = #{postCodeValue}
AND T.AREA = #{areaValue}
