mapper.select:
WITH INS AS ( SELECT DISTINCT A.INSTALL_ENTRY_NO FROM SAL0046D A JOIN SAL0001D B ON A.SALES_ORD_ID = B.SALES_ORD_ID LEFT JOIN sal0046d ie ON ie.sales_ord_id = b.sales_ord_id AND ie.call_entry_id <> 0 JOIN sys0026m stk ON stk.stk_id = ie.install_stk_id
WHERE B.BNDL_ID IS NULL
AND A.INSTALL_ENTRY_NO = #{installNo}
AND B.SALES_ORD_NO = #{orderNo}
AND B.REF_NO = #{orderRefNo}
AND B.CUST_PO_NO = #{poNo}
AND A.INSTALL_DT >= TO_DATE(#{instalStrlDate}, 'DD/MM/YYYY')
AND A.INSTALL_DT <= TO_DATE(#{installEndDate}, 'DD/MM/YYYY')
AND A.INSTALL_DT between TO_DATE(#{instalStrlDate}, 'DD/MM/YYYY') and TO_DATE(#{installEndDate}, 'DD/MM/YYYY')
AND A.APPNT_DT >= TO_DATE(#{startDate}, 'DD/MM/YYYY')
AND A.APPNT_DT <= TO_DATE(#{endDate}, 'DD/MM/YYYY')
AND A.APPNT_DT between TO_DATE(#{startDate}, 'DD/MM/YYYY') and TO_DATE(#{endDate}, 'DD/MM/YYYY')
AND B.SALES_DT = TO_DATE(#{orderDate},'DD/MM/YYYY')
AND STK.STK_CODE = #{product}
)
SELECT DISTINCT CD1.CODE , IE.INSTALL_ENTRY_NO , SOM.SALES_ORD_NO , IE.INSTALL_DT , MEM.MEM_CODE , STK.STK_DESC , SOM.CUST_ID , CUST.NAME AS CUST_NAME , MEM.NAME , CD2.CODE as APP_TYPE , ST.CODE CODE1 , IE.INSTALL_ENTRY_ID , CE.CALL_ENTRY_ID , CD1.CODE_ID CODEID1 , CD1.CODE_NAME , SOM.SALES_ORD_ID SALES_ORD_ID , TO_CHAR (IE.INSTALL_DT, 'DD-MM-YYYY') C3 , CE.DOC_ID C1 , I.BRNCH_ID , BRANCH.CODE BRNCH_CODE , CASE WHEN ORG1.MEM_CODE IS NOT NULL THEN ORG1.MEM_CODE ELSE ORG2.USER_NAME END AS LST_UPD , IE.RCD_TMS , S28.SERIAL_REQUIRE_CHK_YN , CN.TEL_M1 , CN.TEL_O , CN.TEL_R , CUSTADD.ADDR_DTL , CUSTADD.STREET , MA.AREA , MA.CITY , MA.POSTCODE , MA.STATE , CASE WHEN IR.ALLOW_COMM IS NOT NULL THEN IR.ALLOW_COMM ELSE 0 END AS ALLOW_COMMISION , I.INSTCT || DBMS_LOB.SUBSTR(CR.CALL_REM, DBMS_LOB.GETLENGTH(CR.CALL_REM), 1) AS REMARK --, CASE WHEN IE.RESULT_RPT_EMAIL_NO IS NOT NULL THEN IE.RESULT_RPT_EMAIL_NO ELSE CUST.EMAIL END AS RESULT_REP_EMAIL_NO , CUST.EMAIL AS RESULT_REP_EMAIL_NO , IE.EMAIL_SENT_COUNT ,STODELVRSTS.DELVRY_GR_CMPLT sto_delvry_gr ,SMODELVRSTS.DELVRY_GR_CMPLT smo_delvry_gr FROM SAL0001D som JOIN SAL0002D SOD ON SOD.SALES_ORD_ID = som.SALES_ORD_ID JOIN SAL0045D I ON I.SALES_ORD_ID = som.SALES_ORD_ID LEFT JOIN SAL0046D IE ON IE.SALES_ORD_ID = som.SALES_ORD_ID and IE.CALL_ENTRY_ID <> 0 /*By KV - no need data which is call_entry_id = 0 */ LEFT JOIN SAL0047D IR ON IR.ENTRY_ID = IE.INSTALL_ENTRY_ID LEFT JOIN CCR0006D CE ON CE.CALL_ENTRY_ID = IE.CALL_ENTRY_ID LEFT JOIN CCR0007D CR ON CR.CALL_RESULT_ID = CE.RESULT_ID JOIN SYS0026M STK ON STK.STK_ID = ie.INSTALL_STK_ID /* SOD.ITM_STK_ID*/ LEFT JOIN SYS0013M CD1 ON CD1.CODE_ID = CE.TYPE_ID LEFT JOIN SYS0013M CD2 ON CD2.CODE_ID = som.APP_TYPE_ID LEFT JOIN SYS0038M ST ON ST.STUS_CODE_ID = IE.STUS_CODE_ID LEFT JOIN ORG0001D MEM ON MEM.MEM_ID = IE.CT_ID JOIN SYS0005M BRANCH ON BRANCH.BRNCH_ID = I.BRNCH_ID JOIN SAL0023D CUSTADD ON CUSTADD.CUST_ADD_ID = I.ADD_ID JOIN SAL0029D CUST ON CUST.CUST_ID = CUSTADD.CUST_ID LEFT JOIN org0001d ORG1 ON ORG1.MEM_ID = ir.CRT_USER_ID and ORG1.MEM_TYPE > 2 LEFT JOIN SYS0047M ORG2 ON ORG2.USER_ID = ir.CRT_USER_ID AND ORG2.USER_TYPE_ID > 2 LEFT JOIN SYS0028M S28 ON S28.WH_LOC_CODE = MEM.MEM_CODE LEFT JOIN SAL0027D CN ON CN.CUST_CNTC_ID = SOM.CUST_CNT_ID LEFT JOIN SYS0064M MA ON MA.AREA_ID = CUSTADD.AREA_ID LEFT JOIN ( SELECT MAX(GI.REQST_NO),GI.REF_DOC_NO,GI.REQST_STUS,NVL(GR.DELVRY_GR_CMPLT,'N') DELVRY_GR_CMPLT ,CASE WHEN NVL(GR.DELVRY_GR_CMPLT,'N') = 'Y' THEN '1' ELSE '0' END STO_FLAG FROM LOG0047M GI JOIN INS B ON GI.REF_DOC_NO= B.INSTALL_ENTRY_NO LEFT JOIN LOG0048D GIDTL ON GIDTL.REQST_NO = GI.REQST_NO LEFT JOIN LOG0055D GRDTL ON GRDTL.REQST_NO = GIDTL.REQST_NO AND GRDTL.ITM_CODE = GIDTL.ITM_CODE LEFT JOIN LOG0054M GR ON GR.DELVRY_NO = GRDTL.DELVRY_NO WHERE GI.TRNSC_TYPE_DTL = 'US03' AND NVL(GI.REQST_DEL,'N') = 'N' AND GR.DELVRY_NO IS NOT NULL GROUP BY GI.REF_DOC_NO,GI.REQST_STUS,GR.DELVRY_GR_CMPLT ) STODELVRSTS ON STODELVRSTS.REF_DOC_NO = IE.INSTALL_ENTRY_NO LEFT JOIN ( SELECT MAX(GI.REQST_NO),GI.REF_DOC_NO,GI.REQST_STUS,NVL(GR.DELVRY_GR_CMPLT,'N') DELVRY_GR_CMPLT ,CASE WHEN NVL(GR.DELVRY_GR_CMPLT,'N') = 'Y' THEN '2' ELSE '0' END SMO_FLAG FROM LOG0047M GI JOIN INS B ON GI.REF_DOC_NO= B.INSTALL_ENTRY_NO LEFT JOIN LOG0048D GIDTL ON GIDTL.REQST_NO = GI.REQST_NO LEFT JOIN LOG0055D GRDTL ON GRDTL.REQST_NO = GIDTL.REQST_NO AND GRDTL.ITM_CODE = GIDTL.ITM_CODE LEFT JOIN LOG0054M GR ON GR.DELVRY_NO = GRDTL.DELVRY_NO WHERE GI.TRNSC_TYPE_DTL = 'UM03' AND NVL(GI.REQST_DEL,'N') = 'N' AND GR.DELVRY_NO IS NOT NULL GROUP BY GI.REF_DOC_NO,GI.REQST_STUS,GR.DELVRY_GR_CMPLT ) SMODELVRSTS ON SMODELVRSTS.REF_DOC_NO = IE.INSTALL_ENTRY_NO WHERE 1 = 1 AND som.BNDL_ID IS NULL -- homecare Remove(except)
AND ST.STUS_CODE_ID IN
#{item}
AND CD1.CODE_ID IN
#{item}
AND CD2.CODE_ID IN
#{item}
AND IE.INSTALL_ENTRY_NO = #{installNo}
AND SOM.SALES_ORD_NO = #{orderNo}
AND SOM.REF_NO = #{orderRefNo}
AND SOM.CUST_PO_NO =#{poNo}
AND ((CASE WHEN (MEM.MEM_ID IS NOT NULL) THEN MEM.MEM_CODE ELSE '' END) = #{ctCode})
AND I.BRNCH_ID IN
#{item}
AND CUST.CUST_ID = #{customerId}
AND CUST.NAME = #{customerName}
AND CUST.NRIC = #{customerIc}
AND IR.SIRIM_NO = #{sirimNo}
AND IR.SERIAL_NO = #{serialNo}
AND IE.INSTALL_DT >= TO_DATE(#{instalStrlDate}, 'DD/MM/YYYY')
AND IE.INSTALL_DT <= TO_DATE(#{installEndDate}, 'DD/MM/YYYY')
AND IE.INSTALL_DT between TO_DATE(#{instalStrlDate}, 'DD/MM/YYYY') and TO_DATE(#{installEndDate}, 'DD/MM/YYYY')
AND IE.APPNT_DT >= TO_DATE(#{startDate}, 'DD/MM/YYYY')
AND IE.APPNT_DT <= TO_DATE(#{endDate}, 'DD/MM/YYYY')
AND IE.APPNT_DT between TO_DATE(#{startDate}, 'DD/MM/YYYY') and TO_DATE(#{endDate}, 'DD/MM/YYYY')
AND SOM.SALES_DT = TO_DATE(#{orderDate},'DD/MM/YYYY')
AND STK.STK_CODE = #{product}
AND STK.STK_CODE = #{product}
AND STK.STK_CODE = #{product}
AND ( STODELVRSTS.STO_FLAG in
#{item}
or SMODELVRSTS.SMO_FLAG in
#{item}
)

SELECT * FROM (SELECT Project1.INSTALL_ENTRY_ID INSTALL_ENTRY_ID , Project1.C1 C1 , Project1.C2 C2 , Project1.INSTALL_ENTRY_NO INSTALL_ENTRY_NO , Project1.SALES_ORD_ID SALES_ORD_ID , Project1.SALES_ORD_NO SALES_ORD_NO , Project1.REF_NO REF_NO , Project1.CUST_PO_NO CUST_PO_NO , Project1.SALES_DT SALES_DT , Project1.C3 C3 , Project1.STK_DESC STK_DESC , Project1.CODE_ID CODE_ID , Project1.CODE_NAME CODE_NAME , Project1.CALL_ENTRY_ID CALL_ENTRY_ID , Project1.CODEID1 CODEID1 , Project1.CODE1 CODE , Project1.STUS_CODE_ID STUS_CODE_ID , Project1.CODE CODE1 , Project1.BRNCH_ID BRNCH_ID , (SELECT CODE FROM SYS0005M H WHERE H.BRNCH_ID = Project1.BRNCH_ID) BRNCH_CODE , Project1.CUST_ID CUST_ID , Project1.NAME NAME , Project1.NRIC NRIC , Project1.C4 C4 , Project1.C5 C5 , Project1.C6 C6 FROM (SELECT Project1.INSTALL_ENTRY_ID INSTALL_ENTRY_ID , Project1.INSTALL_ENTRY_NO INSTALL_ENTRY_NO , Project1.CALL_ENTRY_ID CALL_ENTRY_ID , Project1.SALES_ORD_ID SALES_ORD_ID , Project1.SALES_ORD_NO SALES_ORD_NO , Project1.REF_NO REF_NO , Project1.SALES_DT SALES_DT , Project1.CUST_PO_NO CUST_PO_NO , Project1.CODE_ID CODE_ID , Project1.CODE_NAME CODE_NAME , Project1.STK_DESC STK_DESC , Project1.CUST_ID CUST_ID , Project1.NAME NAME , Project1.NRIC NRIC , Project1.BRNCH_ID BRNCH_ID , Project1.STUS_CODE_ID STUS_CODE_ID , Project1.CODE CODE , Project1.CODEID1 CODEID1 , Project1.CODE1 CODE1 , Project1.C1 C1 , Project1.C2 C2 , Project1.C3 C3 , Project1.C4 C4 , Project1.C5 C5 , Project1.C6 C6 , ROW_NUMBER() OVER ( ORDER BY Project1.INSTALL_ENTRY_ID ASC ) ROW_NUMBER FROM (SELECT Filter1.INSTALL_ENTRY_ID INSTALL_ENTRY_ID , Filter1.INSTALL_ENTRY_NO INSTALL_ENTRY_NO , Filter1.CALLENTRYID1 CALL_ENTRY_ID , Filter1.SALESORDERID1 SALES_ORD_ID , Filter1.SALES_ORD_NO SALES_ORD_NO , Filter1.REF_NO REF_NO , Filter1.SALES_DT SALES_DT , Filter1.CUST_PO_NO CUST_PO_NO , Filter1.CODEID2 CODE_ID , Filter1.CODENAME1 CODE_NAME , Filter1.STK_DESC STK_DESC , Filter1.CUSTOMERID1 CUST_ID , Filter1.NAME1 NAME , Filter1.NRIC NRIC , Filter1.BRANCHID1 BRNCH_ID , Filter1.STATUSCODEID1 STUS_CODE_ID , Filter1.CODE2 CODE , Filter1.CODEID3 CODEID1 , Filter1.CODE3 CODE1 , CASE WHEN ( Filter1.DOC_ID IS NOT NULL ) THEN Filter1.DOC_ID ELSE 0 END C1 , CASE WHEN ( Extent12.MEM_ID IS NOT NULL ) THEN Extent12.MEM_CODE ELSE '' END C2 , Filter1.INSTALLDATE1 C3 , CASE WHEN ( Filter1.SIRIM_NO IS NOT NULL ) THEN Filter1.SIRIM_NO ELSE '' END C4 , CASE WHEN ( Filter1.SERIAL_NO IS NOT NULL ) THEN Filter1.SERIAL_NO ELSE '' END C5 , CASE WHEN ( Filter1.INSTALLDATE2 IS NOT NULL ) THEN Filter1.INSTALLDATE2 ELSE to_date('1900-01-01','yyyy-mm-dd') END C6 FROM (SELECT Extent1.INSTALL_ENTRY_ID INSTALL_ENTRY_ID , Extent1.INSTALL_ENTRY_NO INSTALL_ENTRY_NO , Extent1.CT_ID CTID1 , Extent1.INSTALL_DT INSTALLDATE1 , Extent1.CALL_ENTRY_ID CALLENTRYID1 , Extent2.SALES_ORD_ID SALESORDERID1 , Extent2.SALES_ORD_NO SALES_ORD_NO , Extent2.REF_NO REF_NO , Extent2.SALES_DT SALES_DT , Extent2.CUST_PO_NO CUST_PO_NO , Extent3.CODE_ID CODEID2 , Extent3.CODE_NAME CODENAME1 , Extent5.STK_DESC STK_DESC , Extent6.CUST_ID CUSTOMERID1 , Extent6.NAME NAME1 , Extent6.NRIC NRIC , Extent7.BRNCH_ID BRANCHID1 , Extent8.STUS_CODE_ID STATUSCODEID1 , Extent8.CODE CODE2 , Extent9.INSTALL_DT INSTALLDATE2 , Extent9.SIRIM_NO SIRIM_NO , Extent9.SERIAL_NO SERIAL_NO , Extent10.DOC_ID DOC_ID , Extent11.CODE_ID CODEID3 , Extent11.CODE CODE3 FROM SAL0046D Extent1 JOIN SAL0001D Extent2 ON Extent2.SALES_ORD_ID = Extent1.SALES_ORD_ID JOIN SYS0013M Extent3 ON Extent3.CODE_ID = Extent2.APP_TYPE_ID JOIN SAL0002D Extent4 ON Extent4.SALES_ORD_ID = Extent2.SALES_ORD_ID JOIN SYS0026M Extent5 ON Extent5.STK_ID = Extent4.ITM_STK_ID JOIN SAL0029D Extent6 ON Extent6.CUST_ID = Extent2.CUST_ID JOIN SAL0045D Extent7 ON Extent7.SALES_ORD_ID = Extent2.SALES_ORD_ID JOIN SYS0038M Extent8 ON Extent8.STUS_CODE_ID = Extent1.STUS_CODE_ID LEFT JOIN SAL0047D Extent9 ON Extent9.ENTRY_ID = Extent1.INSTALL_ENTRY_ID LEFT JOIN CCR0006D Extent10 ON Extent10.CALL_ENTRY_ID = Extent1.CALL_ENTRY_ID LEFT JOIN SYS0013M Extent11 ON Extent11.CODE_ID = Extent10.TYPE_ID WHERE 1 = Extent8.STUS_CODE_ID ) Filter1 LEFT JOIN ORG0001D Extent12 ON Extent12.MEM_ID = Filter1.CTID1 WHERE 1=1
AND Filter1.BRANCHID1 = #{dscCode}
and ( (TO_NUMBER(INSTR(LTRIM(RTRIM(UPPER(Filter1.NAME1))), UPPER(LTRIM(RTRIM(#{customerName})))),99999)) > 0 )
AND ( (TO_NUMBER(INSTR(LTRIM(RTRIM(UPPER(Filter1.NRIC))), UPPER(LTRIM(RTRIM(#{customerIc})))),99999)) > 0 )
AND Filter1.SIRIM_NO = #{sirimNo}
AND Filter1.SERIAL_NO = #{serialNo}
) Project1 ) Project1 WHERE Project1.ROW_NUMBER > 0 ORDER BY Project1.INSTALL_ENTRY_ID ASC )

SELECT CT_ID
FROM SAL0046D
WHERE INSTALL_ENTRY_NO = #{installEntryNo}

SELECT COUNT(INSTALL_ENTRY_ID)
FROM SAL0046D
WHERE INSTALL_ENTRY_NO = #{installEntryNo}
    AND RCD_TMS = #{rcdTms}

SELECT MAX(B.RESULT_ID) AS RESULT_ID
     , COUNT(1) AS RESULT_CNT
FROM SAL0046D A JOIN SAL0047D B ON B.ENTRY_ID = A.INSTALL_ENTRY_ID
WHERE A.INSTALL_ENTRY_ID = #{hidEntryId}
  AND A.SALES_ORD_ID = #{hidSalesOrderId}

SELECT B.SO_EXCHG_ID FROM SAL0001D A
JOIN SAL0004D B ON A.SALES_ORD_ID = B.SO_ID
WHERE A.SALES_ORD_ID= #{salesOrderId}


 SELECT COUNT(*) CNT
 FROM SAL0046D A ,
      ORG0001D B
 WHERE A.INSTALL_ENTRY_NO = #{serviceNo}
   AND A.CT_ID =B.MEM_ID
   AND B.MEM_CODE  =#{userId}

SELECT COUNT(*) COUNT
FROM SAL0046D
WHERE INSTALL_ENTRY_ID = #{installEntryId}
  AND SALES_ORD_ID = #{orderId}
  AND RCD_TMS = #{rcdTms}

