SELECT A.SERIAL_NO CODE, A.SERIAL_NO CODE_NAME
  FROM LOG0100M A
   LEFT JOIN SYS0028M B ON B.WH_LOC_ID = A.LAST_LOC_ID
WHERE B.WH_LOC_CODE = #{ctCode}
    AND A.ITM_CODE = #{stkCode}
    AND A.STUS_CODE = 'I'
 ORDER BY A.SERIAL_NO

SELECT RESULT_TYPE , SALES_ORD_ID , SALES_ORD_NO , TYPE_CODE , TYPE_NO , APP_TYPE_ID , APP_TYPE , STK_CTGRY_ID , PROD_CAT , STK_ID , STK_CODE , PRODUCT , SERIAL_NO , SIRIM_NO , STUS_ID , STATUS , RESULT_NO , NVL(TO_CHAR (APPT_DT,'DD-MM-YYYY'),'-') APPT_DT , NVL(TO_CHAR(COM_DT,'DD-MM-YYYY'),'-') COM_DT , NVL(TO_CHAR(RESULT_KEY_DT,'DD-MM-YYYY'),'-') RESULT_KEY_DT , FAIL_LOC , FAIL_ID , FAIL_TYPE , FAIL_FEEDBACK , NVL(VOLT,'0') VOLT , NVL(PSI,'0') PSI , NVL(LPM,'0') LPM , NVL(BOOST_PUMP,'0') BOOST_PUMP , NVL(AFTER_PSI,'0') AFTER_PSI , NVL(AFTER_LPM,'0') AFTER_LPM , NVL(TDS,'0') TDS , NVL(ROOM_TEMP,'0') ROOM_TEMP , NVL(WATER_SRC_TEMP,'0') WATER_SRC_TEMP, ADPT_CODE , NVL(ADPT_USED,'-') ADPT_USED , SUM_TOTAL_PIC , BRNCH_ID , DSC_BRNCH_CODE , CT_CODE , CT_NAME , CT_HP_NO , CUST_ID , CUST_NAME , CUST_TEL , STATE , CITY , AREA_ID , AREA , POSTCODE, WATER_SRC_TYPE FROM (
SELECT 'AS' RESULT_TYPE , C.SALES_ORD_ID , C.SALES_ORD_NO , A.AS_TYPE_ID TYPE_CODE , E.CODE TYPE_NO, C.APP_TYPE_ID , F.CODE APP_TYPE , G.STK_CTGRY_ID , K.CODE PROD_CAT , G.STK_ID , G.STK_CODE , G.STK_CODE || ' - ' || G.STK_DESC AS PRODUCT , '-' SERIAL_NO , '-' SIRIM_NO , A.AS_STUS_ID STUS_ID , H.CODE STATUS , B.AS_RESULT_NO RESULT_NO , A.AS_APPNT_DT APPT_DT , B.AS_SETL_DT COM_DT , B.AS_RESULT_CRT_DT RESULT_KEY_DT , 0 FAIL_LOC , 0 FAIL_ID , '-' FAIL_TYPE , '-' FAIL_FEEDBACK , 0 VOLT , B.PSI , B.LPM , 0 BOOST_PUMP , 0 AFTER_PSI , 0 AFTER_LPM , 0 TDS , 0 ROOM_TEMP , 0 WATER_SRC_TEMP , '0' ADPT_CODE , '-' ADPT_USED , 0 SUM_TOTAL_PIC , A.AS_BRNCH_ID BRNCH_ID , NVL(Q.CODE, '-') DSC_BRNCH_CODE, J.MEM_CODE CT_CODE , J.NAME CT_NAME , J.TEL_MOBILE CT_HP_NO , C.CUST_ID , L.NAME CUST_NAME , NVL(P.TEL_M1,'-') CUST_TEL , O.AREA_ID , O.STATE , O.AREA , O.CITY , O.POSTCODE, T.CODE_NAME WATER_SRC_TYPE FROM SVC0001D A LEFT JOIN SVC0004D B ON A.AS_ID = B.AS_ENTRY_ID AND B.AS_RESULT_TYPE_ID = 457 AND B.AS_RESULT_STUS_ID NOT IN (19) AND B.AS_RESULT_IS_CURR = 1 LEFT JOIN SAL0001D C ON A.AS_SO_ID = C.SALES_ORD_ID LEFT JOIN SAL0002D D ON C.SALES_ORD_ID = D.SALES_ORD_ID LEFT JOIN SYS0013M E ON A.AS_TYPE_ID = E.CODE_ID LEFT JOIN SYS0013M F ON C.APP_TYPE_ID = F.CODE_ID LEFT JOIN SYS0026M G ON D.ITM_STK_ID = G.STK_ID LEFT JOIN SYS0038M H ON A.AS_STUS_ID = H.STUS_CODE_ID LEFT JOIN ORG0001D J ON A.AS_MEM_ID = J.MEM_ID LEFT JOIN SYS0013M K ON G.STK_CTGRY_ID = K.CODE_ID LEFT JOIN SAL0029D L ON C.CUST_ID = L.CUST_ID AND L.STUS_CODE_ID = 1 LEFT JOIN SAL0045D M ON M.SALES_ORD_ID = C.SALES_ORD_ID AND M.STUS_CODE_ID = 1 LEFT JOIN SAL0023D N ON M.ADD_ID = N.CUST_ADD_ID LEFT JOIN SYS0064M O ON N.AREA_ID = O.AREA_ID LEFT JOIN SAL0027D P ON C.CUST_CNT_ID = P.CUST_CNTC_ID AND P.STUS_CODE_ID = 9 LEFT JOIN SYS0005M Q ON A.AS_BRNCH_ID = Q.BRNCH_ID LEFT JOIN SAL0046D R ON M.SALES_ORD_ID = R.SALES_ORD_ID AND R.CALL_ENTRY_ID NOT IN (0) LEFT JOIN SAL0047D S ON R.INSTALL_ENTRY_ID = S.ENTRY_ID LEFT JOIN SYS0013M T ON T.CODE_ID = S.WATER_SRC_TYPE
WHERE C.BNDL_ID IS NULL AND E.CODE IN ('AAS','MAS') AND H.CODE IN ('COM','FAL','CAN')
AND B.AS_RESULT_CRT_DT >= TO_DATE(#{resultStartDate}, 'DD/MM/YYYY') AND B.AS_RESULT_CRT_DT <= TO_DATE(#{resultEndDate}, 'DD/MM/YYYY')
AND A.AS_APPNT_DT >= TO_DATE(#{appointmentStartDate}, 'DD/MM/YYYY') AND A.AS_APPNT_DT <= TO_DATE(#{appointmentEndDate}, 'DD/MM/YYYY')
AND B.AS_SETL_DT >= TO_DATE(#{comStartDate}, 'DD/MM/YYYY') AND B.AS_SETL_DT <= TO_DATE(#{comEndDate}, 'DD/MM/YYYY')
UNION ALL
SELECT
'INS' RESULT_TYPE , D.SALES_ORD_ID , D.SALES_ORD_NO , F.TYPE_ID TYPE_CODE, G.CODE TYPE_NO, D.APP_TYPE_ID , H.CODE APP_TYPE , I.STK_CTGRY_ID , L.CODE PROD_CAT , I.STK_ID , I.STK_CODE , I.STK_CODE || ' - ' || I.STK_DESC AS PRODUCT , NVL(C.SERIAL_NO,'-') SERIAL_NO , NVL(C.SIRIM_NO,'-') SIRIM_NO , B.STUS_CODE_ID STUS_ID, J.CODE STATUS , B.INSTALL_ENTRY_NO RESULT_NO , B.APPNT_DT APPT_DT , C.INSTALL_DT COM_DT , C.CRT_DT RESULT_KEY_DT , C.FAIL_LOC , C.FAIL_ID , NVL(N.DEFECT_DESC,'-') FAIL_TYPE , NVL(O.DEFECT_CODE || ' - ' || O.DEFECT_DESC , '-') FAIL_FEEDBACK , C.VOLT , C.PSI , C.LPM , 0 BOOST_PUMP , 0 AFTER_PSI , 0 AFTER_LPM , C.TDS , C.ROOM_TEMP , C.WATER_SRC_TEMP , C.ADPT_USED ADPT_CODE , P.CODE_NAME ADPT_USED , NVL(V.CNT, 0) AS SUM_TOTAL_PIC , A.BRNCH_ID , NVL(U.CODE, '-') DSC_BRNCH_CODE, K.MEM_CODE CT_CODE , K.NAME CT_NAME , K.TEL_MOBILE CT_HP_NO , D.CUST_ID , Q.NAME CUST_NAME , NVL( T.TEL_M1,'-') CUST_TEL , S.AREA_ID , S.STATE , S.AREA , S.CITY , S.POSTCODE , W.CODE_NAME WATER_SRC_TYPE FROM SAL0045D A LEFT JOIN SAL0046D B ON A.SALES_ORD_ID = B.SALES_ORD_ID AND B.CALL_ENTRY_ID NOT IN (0) LEFT JOIN SAL0047D C ON B.INSTALL_ENTRY_ID = C.ENTRY_ID LEFT JOIN SAL0001D D ON A.SALES_ORD_ID = D.SALES_ORD_ID LEFT JOIN SAL0002D E ON D.SALES_ORD_ID = E.SALES_ORD_ID LEFT JOIN CCR0006D F ON A.SALES_ORD_ID = F.SALES_ORD_ID AND B.CALL_ENTRY_ID = F.CALL_ENTRY_ID LEFT JOIN SYS0013M G ON F.TYPE_ID = G.CODE_ID LEFT JOIN SYS0013M H ON D.APP_TYPE_ID = H.CODE_ID LEFT JOIN SYS0026M I ON E.ITM_STK_ID = I.STK_ID LEFT JOIN SYS0038M J ON B.STUS_CODE_ID = J.STUS_CODE_ID LEFT JOIN ORG0001D K ON C.CT_ID = K.MEM_ID LEFT JOIN SYS0013M L ON I.STK_CTGRY_ID = L.CODE_ID LEFT JOIN SYS0100M N ON C.FAIL_LOC = N.DEFECT_GRP AND N.DEFECT_TYP = 'IF' LEFT JOIN SYS0100M O ON C.FAIL_LOC = O.DEFECT_GRP AND C.FAIL_ID = O.DEFECT_ID LEFT JOIN SYS0013M P ON C.ADPT_USED = P.CODE_ID LEFT JOIN SAL0029D Q ON D.CUST_ID = Q.CUST_ID AND Q.STUS_CODE_ID = 1 LEFT JOIN SAL0023D R ON A.ADD_ID = R.CUST_ADD_ID LEFT JOIN SYS0064M S ON R.AREA_ID = S.AREA_ID LEFT JOIN SAL0027D T ON D.CUST_CNT_ID = T.CUST_CNTC_ID AND T.STUS_CODE_ID = 9 LEFT JOIN SYS0005M U ON A.BRNCH_ID = U.BRNCH_ID LEFT JOIN SYS0013M W ON W.CODE_ID = C.WATER_SRC_TYPE LEFT JOIN (SELECT COUNT(Z.NO) AS CNT, NO FROM (SELECT SUBSTR(ATCH_FILE_NAME,(INSTR(ATCH_FILE_NAME, '_', 1, 2) + 1),((INSTR(ATCH_FILE_NAME, '_', 1, 3)-1) - INSTR(ATCH_FILE_NAME, '_', 1, 2))) AS NO FROM SYS0071D WHERE FILE_SUB_PATH = 'service/mobile/installation' AND substr(atch_file_name,(instr(atch_file_name, '_', 1, 2) + 1),((instr(atch_file_name, '_', 1, 3)-1) - instr(atch_file_name, '_', 1, 2))) IN ( SELECT to_char(b.install_entry_no) FROM sal0046d b WHERE 1=1
AND B.APPNT_DT >= TO_DATE(#{appointmentStartDate}, 'DD/MM/YYYY') AND B.APPNT_DT <= TO_DATE(#{appointmentEndDate}, 'DD/MM/YYYY')
)) Z WHERE NO IS NOT NULL GROUP BY NO) V ON TO_CHAR(V.NO) = TO_CHAR(B.INSTALL_ENTRY_NO)
WHERE D.BNDL_ID IS NULL AND G.CODE IN ('PEX', 'NEW') AND J.CODE IN ('COM','FAL','CAN')
AND C.CRT_DT >= TO_DATE(#{resultStartDate}, 'DD/MM/YYYY') AND C.CRT_DT <= TO_DATE(#{resultEndDate}, 'DD/MM/YYYY')
AND B.APPNT_DT >= TO_DATE(#{appointmentStartDate}, 'DD/MM/YYYY') AND B.APPNT_DT <= TO_DATE(#{appointmentEndDate}, 'DD/MM/YYYY')
AND C.INSTALL_DT >= TO_DATE(#{comStartDate}, 'DD/MM/YYYY') AND C.INSTALL_DT <= TO_DATE(#{comEndDate}, 'DD/MM/YYYY')
) WHERE 1=1
