    , CRT_DT
    , CRT_USER_ID
    , UPD_DT
    , UPD_USER_ID
    , CALL_MEM
    , EVT_STUS_ID
 )
VALUES(
    #{evtId}
    , CCR0013M_EVT_CONT_ID_SEQ.NEXTVAL
    , (SELECT DISTINCT(EVT_TYPE_ID) FROM CCR0012M)
    , #{contNo}
    , (SELECT DISTINCT(SALES_ORD_ID) FROM SAL0001D WHERE SALES_ORD_NO= #{salesOrdNo})
    , #{salesOrdNo}
    , #{name}
    , SYSDATE
    , #{crtUserId}
    , SYSDATE
    , #{updUserId}
    , #{callMem}
    , 33
)

