mapper.select:
SELECT Project1.MEM_ID CODE_ID , Project1.MEM_CODE|| '-' ||Project1.NAME CODE_NAME , Project1.NAME NAME , Project1.NRIC NRIC , Project1.MEM_TYPE MEM_TYPE FROM ( SELECT Extent1.MEM_ID MEM_ID , Extent1.MEM_CODE MEM_CODE , Extent1.MEM_TYPE MEM_TYPE , Extent1.NAME NAME , Extent1.NRIC NRIC FROM ORG0001D Extent1 JOIN ORG0005D ORG5 ON ORG5.MEM_ID = Extent1.MEM_ID WHERE ( 1 = Extent1.STUS ) AND ( Extent1.MEM_TYPE = 3 ) AND ( ORG5.MEM_LVL= 4 )
and Extent1.BRNCH = #{groupCode}
) Project1 ORDER BY Project1.MEM_CODE ASC
