SELECT A.AREA_ID, B.CT_BRNCH_CODE, B.AREA, B.CITY, B.POSTCODE, B.STATE, A.REQST_NO, TO_CHAR(A.PRIOD_FROM,'YYYY-MM-DD') PRIOD_FROM, TO_CHAR(A.PRIOD_TO,'YYYY-MM-DD') PRIOD_TO, CASE WHEN (A.LOC_TYPE='L') THEN 'Local' WHEN (A.LOC_TYPE='O') THEN 'OutStation' ELSE '' END LOC_TYPE, A.SVC_WEEK, A.CT_SUB_GRP, (SELECT COUNT(AREA_ID) FROM ORG0028M WHERE AREA_ID = A.AREA_ID AND TYPE = 6665) AS NO_SVC_CNT
FROM ORG0019M A INNER JOIN SYS0064M B ON A.AREA_ID = B.AREA_ID AND A.AVAIL_FLAG = 'Y' WHERE A.BRNCH_TYPE = 43
AND B.CT_BRNCH_CODE = #{dscCode}
AND B.AREA_ID = #{areaId}
AND B.AREA like '%' || #{area} || '%'
AND B.CITY = #{city}
AND B.POSTCODE = #{postCode}
AND B.STATE = #{state}
AND B.CT_SUB_GRP = #{ctSubGrp}
ORDER BY CT_SUB_GRP

