mapper.select:
/* com.coway.trust.biz.services.servicePlanning.impl.CTSubGroupListMapper.selectMonthCalendar */
SELECT DECODE(SUN, null, null, #{paramMonth} || SUN) AS CHK_SUN, SUN , DECODE(MON, null, null, #{paramMonth} || MON) AS CHK_MON, MON , DECODE(TUE, null, null, #{paramMonth} || TUE) AS CHK_TUE, TUE , DECODE(WED, null, null, #{paramMonth} || WED) AS CHK_WED, WED , DECODE(THU, null, null, #{paramMonth} || THU) AS CHK_THU, THU , DECODE(FRI, null, null, #{paramMonth} || FRI) AS CHK_FRI, FRI , DECODE(SAT, null, null, #{paramMonth} || SAT) AS CHK_SAT, SAT
FROM (SELECT SUN, MON, TUE, WED, THU, FRI, SAT FROM (SELECT TRUNC (dt + LEVEL - 1, 'd') w , TO_CHAR(dt + LEVEL - 1, 'd') d , TO_CHAR(dt + LEVEL - 1, 'dd') dd FROM (SELECT TO_DATE(#{paramMonth}, 'yyyymm') dt FROM dual) CONNECT BY LEVEL < = LAST_DAY(dt) - dt + 1 ) PIVOT (MIN(dd) FOR d IN(1 SUN, 2 MON, 3 TUE, 4 WED, 5 THU, 6 FRI, 7 SAT) ) ORDER BY w ) A

/* com.coway.trust.biz.services.servicePlanning.impl.CTSubGroupListMapper.selectSubGroupServiceDayList */
SELECT AREA_ID
         , TO_CHAR(NOSCV_DT, 'YYYYMMDD')              AS NOSCV_DT
         , TO_NUMBER(TO_CHAR(NOSCV_DT, 'MM'))      AS NOSCV_MM
        ,  TO_CHAR(NOSCV_DT, 'D')                             AS NOSCV_DAY
 FROM ORG0028M
WHERE TO_CHAR(NOSCV_DT, 'YYYY') = #{paramYear}
   AND AREA_ID = #{paramAreaId}
   AND TYPE = #{paramSubGrpType}

