 )

mapper.select:
SELECT HOLIDAY_SEQ, TO_CHAR (HOLIDAY, 'YYYY-MM-DD') HOLIDAY, CASE WHEN HOLIDAY_TYPE = 'P' THEN 'Public Holiday' ELSE 'State Holiday' END HOLIDAY_TYPE, STATE, HOLIDAY_DESC FROM SYS0081M WHERE 1=1
AND HOLIDAY_TYPE = #{type}
AND HOLIDAY = TO_DATE(#{holidayDt},'DD/MM/YYYY')
AND STATE IN
#{item}

SELECT CASE WHEN B.HOLIDAY_TYPE = 'P' THEN 'Public Holiday' ELSE 'State Holiday' END HOLIDAY_TYPE, B.STATE, B.HOLIDAY, B.HOLIDAY_DESC, B.HOLIDAY_SEQ, B.CT_BRNCH_CODE, B.BRNCH_ID, B.ASSIGN_STATUS, B.Replacement_CT_Pax FROM ( SELECT A.HOLIDAY_TYPE, A.STATE, TO_CHAR(A.HOLIDAY,'YYYY-MM-DD') HOLIDAY, A.HOLIDAY_DESC, A.HOLIDAY_SEQ, A.CT_BRNCH_CODE, F.BRNCH_ID, CASE WHEN COUNT (D.CT_MEM_ID) = 0 THEN 'Active' WHEN COUNT (D.CT_MEM_ID) > 0 THEN 'Complete' END ASSIGN_STATUS, COUNT ( CASE WHEN D.CT_MEM_ID IS NOT NULL THEN 1 END ) AS Replacement_CT_Pax FROM ( Select A.HOLIDAY_TYPE, CASE WHEN A.HOLIDAY_TYPE = 'S' THEN A.STATE ELSE F.STATE END STATE, A.HOLIDAY, A.HOLIDAY_DESC, A.HOLIDAY_SEQ, A.CT_BRNCH_CODE FROM( SELECT DISTINCT A.HOLIDAY_TYPE, A.STATE, A.HOLIDAY, A.HOLIDAY_DESC, A.HOLIDAY_SEQ, CASE WHEN A.HOLIDAY_TYPE = 'S' AND A.STATE = B.STATE THEN B.CT_BRNCH_CODE ELSE E.CODE END CT_BRNCH_CODE FROM SYS0081M A ,(SELECT STATE ,CT_BRNCH_CODE FROM SYS0064M GROUP BY STATE ,CT_BRNCH_CODE ) B ,(SELECT CODE FROM SYS0005M WHERE TYPE_ID=43 GROUP BY CODE ) E WHERE A.STATE = B.STATE(+) ) A , (SELECT A.Area_ID, A.CODE, b.state FROM ( SELECT AREA_ID , CODE FROM SYS0005M WHERE TYPE_ID = '43' ) A , SYS0064M b where a.area_ID = b.area_ID (+) ) F WHERE f.CODE(+) = a.CT_BRNCH_CODE ) A ,ORG0020D D ,SYS0005M F WHERE A.HOLIDAY = D.HOLIDAY(+) AND A.HOLIDAY_TYPE = D.HOLIDAY_TYPE(+) AND A.HOLIDAY_SEQ = D.HOLIDAY_SEQ(+) AND A.CT_BRNCH_CODE = D.BRNCH_CODE(+) AND A.CT_BRNCH_CODE = F.CODE group by A.HOLIDAY_TYPE, A.STATE, A.HOLIDAY, A.HOLIDAY_DESC, A.HOLIDAY_SEQ, A.CT_BRNCH_CODE, F.BRNCH_ID ORDER BY CT_BRNCH_CODE,HOLIDAY_TYPE, STATE, HOLIDAY, HOLIDAY_DESC ) B WHERE 1=1
AND B.ASSIGN_STATUS IN
#{item}
AND B.STATE =#{state1}
AND B.STATE IN
#{item}
AND B.HOLIDAY_TYPE = #{type1}
AND B.HOLIDAY_TYPE = #{type}
AND B.HOLIDAY = TO_CHAR(TO_DATE (#{holidayDt}, 'DD/MM/YYYY'),'YYYY-MM-DD')
AND B.HOLIDAY = #{holiday1}
AND B.CT_BRNCH_CODE IN
#{item}
AND B.HOLIDAY_DESC =#{holidayDesc1}
AND B.HOLIDAY_SEQ =#{holidaySeq1}

mapper.insert:
INSERT INTO ORG0020D(
  HOLIDAY
, HOLIDAY_TYPE
, HOLIDAY_SEQ
, ASIGN_SEQ
, STATE
, BRNCH_CODE
, CT_MEM_ID
)
VALUES(
  TO_DATE(#{holiday},'YYYY-MM-DD')
  , #{holidayType}
  , #{holidaySeq}
  , ORG0020D_ASIGN_SEQ.NEXTVAL
  , #{state}
  , #{branchName}
  , TO_NUMBER(#{memId})
  )

mapper.select:
SELECT B.MEM_CODE, B.NAME, B.MEM_ID,COUNT(A.CT_MEM_ID) AS total_assign_date
FROM ORG0020D A INNER JOIN ORG0001D B ON A.CT_MEM_ID = B.MEM_ID
WHERE HOLIDAY = TO_DATE (#{holiday}, 'YYYY-MM-DD') 
 AND HOLIDAY_TYPE = #{holidayType}
 AND B.BRNCH = #{branchId}
GROUP BY B.MEM_CODE, B.NAME, B.MEM_ID

mapper.delete:
DELETE FROM SYS0081M 
WHERE HOLIDAY =  TO_DATE(#{holiday},'YYYY-MM-DD')
           AND HOLIDAY_TYPE = #{holidayType}
           AND HOLIDAY_SEQ = #{holidaySeq}

mapper.select:
select case when (NEWDATE <  SYSDATE) then 't'  
 else 'f' end CHECKDATE
    from ( 
    select to_date(#{holiday},'YY/MM/DD') as NEWDATE  from Dual 
   )
