mapper.select:
SELECT A.BR_NO AS BR_CDE, B.CODE || ' - ' || B.NAME AS BR, A.ITM_CDE AS ITM_CDE, C.STK_CODE AS STK_CDE, C.STK_DESC AS STK_DESC, QTY AS QTY, TO_CHAR(CRT_DT,'DD/MM/YYYY') AS CRT_DT, USER_NAME AS CRT_BY FROM ( SELECT A.REF_NO, A.BR_NO, A.ITM_CDE, SUM(QTY) AS QTY, MAX(A.CRT_DT) AS CRT_DT, MAX(A.CRT_USR) AS CRT_USR FROM ( SELECT A.REF_NO, A.BR_NO, A.ITM_CDE, CASE WHEN B.MOV_TYP = 1 THEN -B.QTY ELSE B.QTY END AS QTY, A.CRT_DT AS CRT_DT, A.CRT_USR AS CRT_USR FROM LOG0089M A JOIN LOG0091D B ON A.REF_NO = B.REF_PFX_NO ) A GROUP BY A.REF_NO, A.BR_NO, A.ITM_CDE ) A LEFT JOIN SYS0005M B ON A.BR_NO = B.BRNCH_ID LEFT JOIN SYS0026M C ON A.ITM_CDE = C.STK_ID LEFT JOIN SYS0047M D ON A.CRT_USR = D.USER_ID WHERE 1 = 1
AND A.BR_NO = #{cboBch}
AND A.ITM_CDE = #{cboItm}
AND ( A.CRT_DT >= TO_DATE(#{trxStrDate} ,'dd/mm/yyyy'))
AND ( A.CRT_DT <= TO_DATE( #{trxEndDate},'dd/mm/yyyy') + 1 )

SELECT CODE || ' - ' || NAME AS CODE_NAME
FROM SYS0005M
WHERE BRNCH_ID = #{params}

SELECT STK_CODE
FROM SYS0026M
WHERE STK_ID = #{params}

SELECT STK_DESC
FROM SYS0026M
WHERE STK_ID = #{params}

SELECT REF_NO,
       SEQ_NO
FROM LOG0089M
WHERE BR_NO = #{BR}
  AND ITM_CDE = #{ITM_CDE}

mapper.update:
UPDATE LOG0089M SET UPD_USR = #{updator}
                  , UPD_DT = SYSDATE
                  , RCD_TMS = TO_CHAR(SYSTIMESTAMP,'ddmmyyyyHH24miss')
WHERE SEQ_NO = #{seqNo}
