mapper.select:
SELECT C.CUST_ID , C.NAME AS CUST_NAME , B.SALES_ORD_NO , A.NO AS HS_NO , TO_CHAR(D.SETL_DT,'DD/MM/YYYY') AS COMPLETE_DT , E.CODE AS SS_STUS , P.CODE_NAME AS DELVY_STUS , TO_CHAR(O.SS_DELIVRY_DT,'DD/MM/YYYY') AS DELIVRY_DT , NVL(Q.SS_RTN_NO,'-') SS_RTN_NO , TO_CHAR(Q.CRT_DT,'DD/MM/YYYY') AS SS_RTN_DT , A.CRT_USER_ID , H.USER_NAME CRT_USR , TO_CHAR(A.CRT_DT, 'DD/MM/YYYY') CRT_DT , A.UPD_USER_ID , I.USER_NAME UPD_USR , TO_CHAR(A.UPD_DT, 'DD/MM/YYYY') UPD_DT , A.SCHDUL_ID , A.SALES_ORD_ID , B.CUST_CNT_ID , B.CUST_ADD_ID , K.STK_ID , K.STK_DESC , M.ADDR_DTL , M.STREET , N.STATE , N.CITY , N.POSTCODE , N.AREA , R.TEL_M1 AS PHONE, R.EMAIL , NVL(O.SS_RESULT_ID,0) SS_RESULT_ID , CASE WHEN A.STUS_CODE_ID = 21 AND D.FAIL_RESN_ID != 0 THEN S.RESN_DESC WHEN A.STUS_CODE_ID = 21 AND D.FAIL_RESN_ID = 0 THEN 'DHL Fail to Delivery' ELSE '-' END AS FAIL_RESN FROM SVC0008D A LEFT JOIN SAL0001D B ON B.SALES_ORD_ID = A.SALES_ORD_ID LEFT JOIN SAL0029D C ON B.CUST_ID = C.CUST_ID LEFT JOIN SVC0006D D ON A.SCHDUL_ID = D.SCHDUL_ID AND D.RESULT_IS_CURR = 1 LEFT JOIN SYS0038M E ON A.STUS_CODE_ID = E.STUS_CODE_ID LEFT JOIN SYS0047M H ON A.CRT_USER_ID = H.USER_ID LEFT JOIN SYS0047M I ON A.UPD_USER_ID = I.USER_ID LEFT JOIN SAL0002D J ON B.SALES_ORD_ID = J.SALES_ORD_ID LEFT JOIN SYS0026M K ON J.ITM_STK_ID = K.STK_ID JOIN SAL0090D L ON A.SALES_ORD_ID = L.SRV_SO_ID LEFT JOIN SAL0023D M ON B.CUST_ADD_ID = M.CUST_ADD_ID LEFT JOIN SYS0064M N ON M.AREA_ID = N.AREA_ID LEFT JOIN SVC0144M O ON A.SCHDUL_ID = O.SCHDUL_ID LEFT JOIN SYS0013M P ON O.SS_DELIVRY_STUS_ID = P.CODE AND P.CODE_MASTER_ID = 585 LEFT JOIN SVC0146M Q ON A.SCHDUL_ID = Q.SS_REF_ID LEFT JOIN SAL0027D R ON B.CUST_CNT_ID = R.CUST_CNTC_ID LEFT JOIN SYS0032M S ON D.FAIL_RESN_ID = S.RESN_ID AND S.RESN_TYPE_ID = 7692 AND S.STUS_CODE_ID = 1 WHERE L.SRV_STUS_ID = 1
AND L.SRV_TYPE = 'SS' AND A.TYPE_ID = 7660
AND A.YEAR = TO_CHAR(TO_DATE(#{mySSMonth} ,'MM/YYYY'),'YYYY') AND A.MONTH = TO_CHAR(TO_DATE(#{mySSMonth},'MM/YYYY'),'MM')
AND B.PV_MONTH = TO_CHAR(TO_DATE(#{myInstallMonth},'MM/YYYY'),'MM') AND B.PV_YEAR = TO_CHAR(TO_DATE(#{myInstallMonth},'MM/YYYY'),'YYYY')
AND C.CUST_ID = #{txtCustomerId}
AND A.NO = #{txtHsOrder}
AND B.SALES_ORD_NO = #{txtSalesOrder}
AND D.SETL_DT BETWEEN TO_DATE(#{completeStartDt} || ' 00:00:00', 'DD/MM/YYYY HH24:MI:SS') AND TO_DATE(#{completeEndDt} || ' 23:59:59', 'DD/MM/YYYY HH24:MI:SS')
AND O.SS_DELIVRY_STUS_ID IN
#{item}
AND A.STUS_CODE_ID = #{cmbSsStatus}
AND N.POSTCODE = #{postCode}

mapper.update:
UPDATE SVC0145D SET FILTER_BARCD_SERIAL_NO = #{serialNo}
                               , SS_RESULT_UPD_USER_ID = #{updUsrId}
                               , SS_RESULT_UPD_DT = SYSDATE
WHERE SS_RESULT_ITM_ID = #{ssResultItmId}
   AND SS_RESULT_PART_ID = #{stkId}

mapper.select:
SELECT Distinct1.C1 C1, Distinct1.STK_ID STK_ID, Distinct1.STK_CODE STK_CODE, Distinct1.STK_DESC STK_DESC, Distinct1.SERIAL_CHK, Distinct1.SERIAL_NO, Distinct1.SS_RESULT_ID, Distinct1.SS_RESULT_ITM_ID, Distinct1.OLD_SERIAL_NO, Distinct1.SRV_FILTER_PRV_CHG_DT FROM ( SELECT Extent1.SALES_ORD_ID, Extent4.STK_ID STK_ID, Extent4.STK_CODE STK_CODE, Extent4.STK_DESC STK_DESC, Extent4.SERIAL_CHK, Extent3.SS_RESULT_PART_QTY C1, Extent3.FILTER_BARCD_SERIAL_NO SERIAL_NO, Extent3.OLD_FILTER_BARCD_SERIAL_NO OLD_SERIAL_NO , Extent3.SS_RESULT_ID, Extent3.SS_RESULT_ITM_ID, Extent3.SRV_FILTER_PRV_CHG_DT FROM SVC0008D Extent1 LEFT JOIN SVC0144M Extent2 ON Extent1.SCHDUL_ID = Extent2.SCHDUL_ID LEFT JOIN SVC0145D Extent3 ON Extent2.SS_RESULT_ID = Extent3.SS_RESULT_ID LEFT JOIN SYS0026M Extent4 ON Extent3.SS_RESULT_PART_ID = Extent4.STK_ID
WHERE Extent1.SALES_ORD_ID = #{salesOrdId} AND Extent1.SCHDUL_ID = #{schdulId} AND Extent2.SS_RESULT_ID = #{ssResultId}
) Distinct1

mapper.update:
 UPDATE SVC0147D SET SS_RTN_GDS = #{totGoodCond}
 					, SS_RTN_MISS_GDS = #{totMiaCond}
						, UPD_USR_ID = #{crtUsrId}
						, UPD_DT = SYSDATE
 WHERE
		SS_RTN_ITM_ID = #{ssRtnItmId}
		AND SS_STK_ID = #{stkId}
		AND DEL_FLG = 'N'

 UPDATE SVC0146M SET DEL_FLG = 'Y'
						, UPD_USR_ID = #{crtUsrId}
						, UPD_DT = SYSDATE
 WHERE
		SS_REF_ID = #{schdulId}
		AND SS_RTN_ID = #{ssRtnId}
		AND DEL_FLG = 'N'

UPDATE SVC0147D SET DEL_FLG = 'Y'
                               , UPD_USR_ID = #{crtUsrId}
                               , UPD_DT = SYSDATE
WHERE SS_RTN_ID = #{ssRtnId}
    AND DEL_FLG = 'N'

   UPDATE SVC0008D
		SET STUS_CODE_ID = #{ssStusId},
			REM = #{ssRemark},
			UPD_DT = SYSDATE ,
			UPD_USER_ID = #{updUsrId}
   WHERE SCHDUL_ID = #{schdulId}

   UPDATE SVC0144M
		SET SS_RESULT_STUS_CODE_ID = #{ssStusId},
			SS_FAIL_RESN_ID = #{ssFailReason},
			SS_RESULT_REM = #{ssRemark},
			SS_RESULT_UPD_DT = SYSDATE ,
			SS_RESULT_UPD_USER_ID = #{updUsrId}
   WHERE SCHDUL_ID = #{schdulId}
  		AND SS_RESULT_ID = #{ssResultId}
