SELECT Extent2.SUP_CUR_SEQNO AS COUNSELING_NO, Extent1.SUP_REF_NO AS SUP_REF_NO, Extent1.SUP_REF_ID AS SUP_REF_ID, Extent3.NAME AS CUST_NAME, TO_CHAR(Extent2.CRT_DT, 'DD-MM-YYYY') AS TAG_REGISTER_DT, Extent4.CALL_ENTRY_ID AS CCR0006D_CALL_ENTRY_ID_SEQ, Extent4.STUS_CODE_ID AS TAG_STATUS_ID, Extent5.NAME AS TAG_STATUS, Extent2.SUP_TAG_MAIN_INQ AS MAIN_TOPIC_ID, Extent2.SUP_TAG_SUB_INQ AS SUB_TOPIC_ID, UPPER(Extent6.DEFECT_CODE || '-' || Extent6.DEFECT_DESC) AS MAIN_TOPIC, UPPER(Extent7.DEFECT_CODE || '-' || Extent7.DEFECT_DESC) AS SUB_TOPIC, UPPER(Extent8.CODE || '-' || Extent8.CODE_NAME) AS INCHG_DEPT, UPPER(Extent9.CODE || '-' || Extent9.CODE_NAME) AS SUB_DEPT FROM SUP0001M Extent1 JOIN SUP0006M Extent2 ON Extent2.SUP_TAG_ORD_ID = Extent1.SUP_REF_ID JOIN SAL0029D Extent3 ON Extent3.CUST_ID = Extent1.CUST_ID JOIN CCR0006D Extent4 ON Extent4.CUR_SEQ_NO = Extent2.SUP_CUR_SEQNO JOIN SYS0038M Extent5 ON Extent5.STUS_CODE_ID = Extent4.STUS_CODE_ID LEFT JOIN SYS0100M Extent6 ON Extent6.DEFECT_ID = Extent2.SUP_TAG_MAIN_INQ AND Extent6.DEFECT_TYP = 'SMI' LEFT JOIN SYS0100M Extent7 ON Extent7.DEFECT_ID = Extent2.SUP_TAG_SUB_INQ AND Extent7.DEFECT_TYP = 'SMI' LEFT JOIN SYS0013M Extent8 ON Extent8.CODE_MASTER_ID = '359' AND Extent8.CODE = Extent4.MAIN_DEPT LEFT JOIN SYS0013M Extent9 ON Extent9.CODE_MASTER_ID = '360' AND Extent9.CODE = Extent4.SUB_DEPT WHERE 1 = 1
AND Extent1.SUP_REF_NO = #{supRefNo}
AND Extent2.SUP_CUR_SEQNO = #{counselingNo}
AND Extent4.STUS_CODE_ID = #{tagStus}
AND Extent2.CRT_DT >= TO_DATE(#{sDate}||' 00:00:00', 'DD-MM-YYYY HH24:MI:SS')
AND Extent2.CRT_DT <= TO_DATE(#{eDate} ||' 23:59:59', 'DD-MM-YYYY HH24:MI:SS')
AND Extent2.SUP_TAG_MAIN_INQ = #{mainTopic}
AND Extent2.SUP_TAG_SUB_INQ = #{ddSubTopic}
AND Extent4.MAIN_DEPT = #{inchgDept}
AND Extent4.SUB_DEPT = #{ddSubDept}
AND Extent3.NAME = #{custName}


 SELECT DEFECT_TYP AS DEFECT_TYPE_CODE,
        DEFECT_ID AS CODE_ID,
        UPPER(DEFECT_CODE || '-' || DEFECT_DESC) AS CODE_NAME
 FROM SYS0100M
 WHERE DEFECT_GRP = #{DEFECT_GRP}
 AND DEFECT_ID != #{DEFECT_GRP}

SELECT Extent2.SUP_CUR_SEQNO AS COUNSELING_NO, Extent1.SUP_REF_ID AS SUP_REF_ID, Extent1.SUP_REF_NO AS SUP_REF_NO, Extent1.CUST_ID AS CUST_ID, Extent3.NAME AS CUST_NAME, Extent2.CRT_DT AS TAG_REGISTER_DT, Extent4.CALL_ENTRY_ID AS CCR0006D_CALL_ENTRY_ID_SEQ, Extent4.STUS_CODE_ID AS TAG_STATUS_ID, Extent5.NAME AS TAG_STATUS, Extent2.SUP_TAG_MAIN_INQ AS MAIN_TOPIC_ID, Extent6.DEFECT_DESC AS MAIN_TOPIC, Extent2.SUP_TAG_SUB_INQ AS SUB_TOPIC_ID, Extent7.DEFECT_DESC AS SUB_TOPIC, Extent4.MAIN_DEPT AS INCHG_DEPT_ID, Extent8.DEFECT_DESC AS INCHG_DEPT_NAME, Extent4.SUB_DEPT AS SUB_DEPT_ID, Extent8.DEFECT_DESC AS SUB_DEPT_NAME, Extent2.SUP_TAG_FL_ATT_ID AS CARELINE_ATCH
FROM SUP0001M Extent1 JOIN SUP0006M Extent2 ON Extent2.SUP_TAG_ORD_ID = Extent1.SUP_REF_ID JOIN SAL0029D Extent3 ON Extent3.CUST_ID = Extent1.CUST_ID JOIN CCR0006D Extent4 ON Extent4.CUR_SEQ_NO = Extent2.SUP_CUR_SEQNO JOIN SYS0038M Extent5 ON Extent5.STUS_CODE_ID = Extent4.STUS_CODE_ID LEFT JOIN SYS0100M Extent6 ON Extent6.DEFECT_ID = Extent2.SUP_TAG_MAIN_INQ AND Extent6.DEFECT_TYP = 'SMI' LEFT JOIN SYS0100M Extent7 ON Extent7.DEFECT_ID = Extent2.SUP_TAG_SUB_INQ AND Extent7.DEFECT_TYP = 'SMI' LEFT JOIN SYS0100M Extent8 ON Extent8.DEFECT_CODE = Extent4.MAIN_DEPT AND Extent8.DEFECT_TYP = 'ICD' LEFT JOIN SYS0100M Extent9 ON Extent9.DEFECT_CODE = Extent4.SUB_DEPT AND Extent8.DEFECT_TYP = 'ICD'
WHERE 1=1 AND Extent1.DEL_FLG = 'N' AND Extent1.SUP_REF_ID = #{supRefId} AND Extent2.SUP_CUR_SEQNO = #{counselingNo}

SELECT A.SUP_REF_ID, A.SUP_REF_NO, TO_CHAR(A.SUP_REF_DT, 'DD-MM-YYYY') AS ORD_DT, B.NAME AS SUP_REF_STUS, C.NAME AS CUST_NAME, C.NRIC AS CUST_NRIC FROM SUP0001M A JOIN SYS0038M B ON A.SUP_REF_STUS = B.STUS_CODE_ID JOIN SAL0029D C ON A.CUST_ID = C.CUST_ID
WHERE 1 = 1 AND A.DEL_FLG = 'N'
AND A.SUP_REF_NO = #{searchOrdNo}
AND TO_CHAR(A.SUP_REF_DT, 'YYYYMMDD') = TO_CHAR(TO_DATE(#{searchOrdDate}, 'DD/MM/YYYY'), 'YYYYMMDD')
AND C.NAME = #{searchOrdCustName}
AND C.NRIC = #{searchOrdCustNric}

