SELECT TO_CHAR(A.SUP_DEL_DT, 'DD-MM-YYYY hh:mi:ss AM') AS DEL_DT, UPPER(B.CODE_NAME) AS DEL_STUS, UPPER(A.SUP_DEL_LOC) AS DEL_LOC, UPPER(A.SUP_DEL_RMK) AS RMK FROM SUP0005D A LEFT JOIN SYS0013M B ON B.CODE_MASTER_ID = '585' AND B.CODE = A.SUP_DEL_SEQ
JOIN SUP0001M C ON C.SUP_REF_ID = A.SUP_REF_ID AND C.SUP_REF_PCL_TRKNO = A.SUP_REF_PCL_TRKNO
JOIN SUP0001M C ON C.SUP_REF_ID = A.SUP_REF_ID AND C.SUP_REF_RTN_CSIGNO = A.SUP_REF_PCL_TRKNO
WHERE A.DEL_FLG = 'N' AND A.SUP_REF_ID = #{supRefId}
AND A.SUP_REF_PCL_TRKNO = #{trkNo}
AND A.SUP_REF_PCL_TRKNO = #{rtnTrkNo}
ORDER BY A.SUP_DEL_DT DESC

