SELECT Extent1.POS_ID , Extent1.POS_NO , Extent1.POS_CUST_NAME , TO_CHAR(Extent1.POS_DT, 'DD-MM-YYYY') POS_DT, Extent1.POS_TYPE_ID , Extent1.POS_MODULE_TYPE_ID , TO_CHAR(Extent1.POS_TOT_AMT , 'FM9999999999990.00') AS POS_TOT_AMT , Extent1.STUS_ID, EXTENT11.NAME STUS_DESC , Extent1.POS_WH_ID , Extent1.POS_MEM_ID , Extent1.POS_CRT_USER_ID , Extent2.CODE_NAME , Extent3.CODE_NAME CODE_NAME1 , Extent6.USER_NAME , CASE WHEN ( Extent5.WH_LOC_ID IS NOT NULL ) THEN Extent5.WH_LOC_CODE ELSE '' END WH_LOC_CODE , CASE WHEN ( Extent5.WH_LOC_ID IS NOT NULL ) THEN Extent5.WH_LOC_DESC ELSE NULL END WH_LOC_DESC , CASE WHEN Extent1.POS_MODULE_TYPE_ID =6795 THEN Extent12.USER_FULL_NAME WHEN (Extent4.MEM_ID IS NOT NULL) THEN Extent4.MEM_CODE || ' - ' || Extent4.NAME ELSE 'CASH' END NAME , CASE WHEN ( NOT ( ( Extent7.TAX_INVC_ID IS NULL ) AND ( Extent7.TAX_INVC_REF_NO IS NULL ) ) ) THEN Extent7.TAX_INVC_REF_NO ELSE '' END TAX_INVC_REF_NO, extent10.MEMO_ADJ_REF_NO, Extent1.BRNCH_ID, Extent9.CODE BRNCH_DESC, UPPER(Extent1.POS_REM) REV_RMK FROM SAL0057D Extent1 JOIN SYS0013M Extent2 ON Extent2.CODE_ID = Extent1.POS_MODULE_TYPE_ID JOIN SYS0013M Extent3 ON Extent3.CODE_ID = Extent1.POS_TYPE_ID LEFT JOIN ORG0001D Extent4 ON Extent4.MEM_ID = Extent1.POS_MEM_ID LEFT JOIN SYS0028M Extent5 ON Extent5.WH_LOC_ID = Extent1.POS_WH_ID JOIN SYS0047M Extent6 ON Extent6.USER_ID = Extent1.POS_CRT_USER_ID LEFT JOIN PAY0031D Extent7 ON Extent7.TAX_INVC_SVC_NO = Extent1.POS_NO LEFT JOIN SYS0005M Extent9 ON Extent9.BRNCH_ID = Extent1.BRNCH_ID LEFT JOIN PAY0011D extent10 ON extent10.MEMO_ADJ_INVC_NO = extent7.tax_invc_ref_no AND MEMO_ADJ_STUS_ID = 4 LEFT JOIN SYS0038M EXTENT11 ON EXTENT1.STUS_ID = EXTENT11.STUS_CODE_ID LEFT JOIN SYS0047M Extent12 ON Extent1.POS_CUST_NAME = Extent12.USER_NAME
LEFT JOIN (SELECT DISTINCT POS_ID, MEM_CODE, NRIC FROM SAL0058D) Extent8 ON Extent8.POS_ID = Extent1.POS_ID
WHERE 1=1
AND Extent1.POS_MODULE_TYPE_ID = #{posModuleTypeId}
AND Extent1.POS_TYPE_ID IN
#{item}
AND Extent1.STUS_ID IN
#{item}
