mapper.select:
SELECT B.INVC_NO, B.CLAM_UN, NVL(TO_CHAR(B.INVC_DT, 'DD/MM/YYYY'), '') AS INVC_DT, NVL(B.SUPPLIR, '-') AS SUPPLIR_NAME, NVL(B.GST_RGIST_NO, '-') AS GST_RGIST_NO, NVL(B.INVC_TYPE, '-') AS INVC_TYPE_NAME, 'MYR' AS CUR, SUM(B.NET_AMT) AS GST_BEFOR_AMT, SUM(B.TAX_AMT) AS GST_AMT, SUM(B.TAX_NON_CLM_AMT) AS TAX_NON_CLM_AMT, SUM(B.TOT_AMT) AS TOT_AMT, B.ATCH_FILE_GRP_ID, CASE WHEN B.INVC_DT IS NULL THEN 'CM' ELSE 'NC' END AS CLAIM_TYPE FROM FCM0020D B LEFT JOIN FCM0007C C ON B.TAX_CODE = C.TAX_CODE WHERE B.CLM_NO = #{claimNo}
AND B.CLM_FLG <> 'Y'
GROUP BY B.INVC_NO, B.CLAM_UN, NVL(TO_CHAR(B.INVC_DT, 'DD/MM/YYYY'), ''), NVL(B.SUPPLIR, '-'), NVL(B.GST_RGIST_NO, '-'), NVL(B.INVC_TYPE, '-'), B.ATCH_FILE_GRP_ID, CASE WHEN B.INVC_DT IS NULL THEN 'CM' ELSE 'NC' END ORDER BY B.CLAM_UN

/* staffClaimMapper.selectStaffClaimInfo */ SELECT T1.CLM_SEQ, T3.COST_CENTR, T3.COST_CENTR_NAME, T3.MEM_ACC_ID, T4.MEM_ACC_NAME, T3.BANK_CODE, T4.BANK_NAME, T3.BANK_ACC_NO, TO_CHAR(TO_DATE(T3.CLM_MONTH, 'YYYYMM'), 'MM/YYYY') AS CLM_MONTH, T3.TOT_AMT AS ALL_TOT_AMT, T1.EXP_GRP, T3.APPV_PRCSS_NO, T1.EXP_TYPE, T1.EXP_TYPE_NAME, T1.GL_ACC_CODE, T1.GL_ACC_CODE_NAME, T1.BUDGET_CODE, T1.BUDGET_CODE_NAME, T5.MEM_ACC_ID AS SUPPLIR, T1.SUPPLIR AS SUPPLIR_NAME, T1.INVC_TYPE, T1.INVC_NO, TO_CHAR(T1.INVC_DT, 'DD/MM/YYYY') AS INVC_DT, T1.GST_RGIST_NO, T1.TAX_CODE, 'MYR' AS CUR, T1.NET_AMT AS GST_BEFOR_AMT, T1.TAX_AMT AS GST_AMT, T1.TAX_NON_CLM_AMT, T1.TOT_AMT, T1.ATCH_FILE_GRP_ID, REGEXP_REPLACE(T1.EXP_DESC, '(^[[:space:]]+)|([[:space:]]+$)', NULL) EXP_DESC FROM FCM0020D T1 LEFT JOIN FCM0019M T3 ON T1.CLM_NO = T3.CLM_NO LEFT JOIN FCM0006M T4 ON T3.MEM_ACC_ID = T4.MEM_ACC_ID LEFT JOIN FCM0006M T5 ON T1.SUPPLIR = T4.MEM_ACC_NAME WHERE T1.CLM_NO = #{clmNo}
AND T1.CLM_SEQ = #{clmSeq}
AND T1.CLAM_UN = #{clamUn} AND ROWNUM = 1
