mapper.select:
SELECT B.INVC_NO, B.CLAM_UN, NVL(TO_CHAR(B.INVC_DT, 'DD/MM/YYYY'), '') AS INVC_DT, NVL(B.SUPPLIR, '-') AS SUPPLIR_NAME, NVL(B.GST_RGIST_NO, '-') AS GST_RGIST_NO, NVL(B.INVC_TYPE, '-') AS INVC_TYPE_NAME, 'MYR' AS CUR, SUM(B.NET_AMT) AS GST_BEFOR_AMT, SUM(B.TAX_AMT) AS GST_AMT, SUM(B.TAX_NON_CLM_AMT) AS TAX_NON_CLM_AMT, SUM(B.TOT_AMT) AS TOT_AMT, B.ATCH_FILE_GRP_ID, CASE WHEN B.INVC_DT IS NULL THEN 'CM' ELSE 'NC' END AS CLAIM_TYPE FROM FCM0020D B LEFT JOIN FCM0007C C ON B.TAX_CODE = C.TAX_CODE WHERE B.CLM_NO = #{claimNo}
AND B.CLM_FLG <> 'Y'
GROUP BY B.INVC_NO, B.CLAM_UN, NVL(TO_CHAR(B.INVC_DT, 'DD/MM/YYYY'), ''), NVL(B.SUPPLIR, '-'), NVL(B.GST_RGIST_NO, '-'), NVL(B.INVC_TYPE, '-'), B.ATCH_FILE_GRP_ID, CASE WHEN B.INVC_DT IS NULL THEN 'CM' ELSE 'NC' END ORDER BY B.CLAM_UN
