mapper.select:
		SELECT
		  COUNT(*) cnt

		  ,B.EMPLY_LEV

		FROM ORG0001D A

		JOIN CMM0006T B ON A.MEM_CODE = B.EMPLY_CODE

		WHERE 1=1

			AND MEM_CODE = #{salesPersonCd}


	      AND MEM_TYPE = #{memType}


          AND B.COMM_TASK_ID = #{taskIDConf}
           GROUP BY B.EMPLY_LEV

SELECT
    t2.MEM_ID AS MEM_ID,
    t2.MEM_UP_ID AS MEM_UP_ID,
    t2.MEM_LVL AS MEM_LVL,
    t2.ORG_CODE AS ORG_CODE,
    t2.GRP_CODE AS GRP_CODE,
    t2.DEPT_CODE AS DEPT_CODE,
    t2.HIST_MONTH AS HIST_MONTH,
    t2.HIST_YEAR AS HIST_YEAR
FROM ORG0001D  t1
JOIN ORG1002V  t2 ON t2.MEM_ID = t1.MEM_ID
                 AND t2.HIST_MONTH = #{pvMonth}
                 AND t2.HIST_YEAR = #{pvYear}
    WHERE 1 = 1
      AND t1.MEM_TYPE = #{typeCode}
       AND T2.ORG_CODE = #{orgCode} 
       AND T2.GRP_CODE = #{grpCode}
       AND T2.DEPT_CODE = #{deptCode}
       AND t1.MEM_CODE =#{memCode} 
      ORDER BY T2.MEM_LVL
      FETCH NEXT 1 ROW ONLY

SELECT T2.CODE_MASTER_ID AS mstId , T1.CODE_MASTER_NAME AS mstNm, T2.CODE_ID AS cdId, T2.CODE AS cd, T2.CODE_NAME AS cdNm, T2.CODE_DESC AS cdDs FROM SYS0012M T1 JOIN SYS0013M T2 ON T1.CODE_MASTER_ID = T2.CODE_MASTER_ID WHERE 1=1
AND T1.CODE_MASTER_ID = #{mstId}
ORDER BY T2.CODE_ID

