AND PV_YEAR = EXTRACT( YEAR FROM TO_DATE(#{netSalesMonth},'MM/YYYY')) AND PV_MONTH = EXTRACT( MONTH FROM TO_DATE(#{netSalesMonth},'MM/YYYY'))
GROUP BY PV_YEAR, PV_MONTH,MEM_ID ) SELECT A.MEM_CODE , E.FULL_NAME , A.DEPT_CODE , A.GRP_CODE , CASE WHEN A.ORG_CODE = ' ' THEN 'CRS0002' ELSE A.ORG_CODE END ORG_CODE , A.MEM_LVL , CASE WHEN A.MEM_LVL = 0 THEN 'SGM' WHEN A.MEM_LVL = 1 THEN 'GM' WHEN A.MEM_LVL = 2 THEN 'SM' WHEN A.MEM_LVL = 3 THEN 'HM' WHEN A.MEM_LVL = 4 THEN 'HP' END POSITION , A.TYPE , A.YEAR , A.MONTH , SUM(NVL(A.W1,0)) W1 , SUM(NVL(A.W2,0)) W2 , SUM(NVL(A.W3,0)) W3 , SUM(NVL(A.W4,0)) W4 , SUM(NVL(A.W1,0)+NVL(A.W2,0)+NVL(A.W3,0)+NVL(A.W4,0)) TOTAL_AMT , A.TARGETATMT_NS , A.PARTTIME , A.NEW_NEO_ACT , A.PARTTIME_NET , A.NEO_PRO , A.NEO_PRO_NET , A.ACT_HP , A.RECRUIT , A.DIRECT_RECRUIT , A.SHI , F.TARGETATMT_SHI
