AND UPPER(A.MEM_LVL) LIKE UPPER('%' || #{memLvl} || '%')
AND TO_CHAR(TO_DATE(A.YEAR || A.MONTH,'YYYYMM'),'YYYYMM') = #{currentYm}
AND A.YEAR = EXTRACT( YEAR FROM TO_DATE(#{netSalesMonth},'MM/YYYY')) AND A.MONTH = EXTRACT(MONTH FROM TO_DATE(#{netSalesMonth},'MM/YYYY'))
