mapper.select:
SELECT FILTER1.INV_REQ_ID , FILTER1.INV_REQ_NO , FILTER1.SO_ID , FILTER1.INV_REQ_STUS_ID , FILTER1.INV_REQ_PARTY_ID , FILTER1.INV_REQ_REM , FILTER1.INV_REQ_CRT_DT , FILTER1.INV_REQ_CRT_USER_ID , FILTER1.INV_REQ_UPD_USER_ID , FILTER1.INV_REQ_USER_ID_AUTO , FILTER1.INV_REQ_HAS_ATTACH , EXTENT5.SALES_ORD_NO , FILTER1.INV_REQ_UPD_DT , CASE WHEN ( FILTER1.INV_REQ_ATTACH_FILE IS NULL ) THEN '' ELSE FILTER1.INV_REQ_ATTACH_FILE END INV_REQ_ATTACH_FILE , FILTER1.CODE2 CODE , FILTER1.INV_REQ_STUS_NAME , FILTER1.CODE3 CODE1 , FILTER1.INV_REQ_PARTY_NAME , FILTER1.INV_REQ_CRT_USER_NAME
, FILTER1.LAST_ORG_CODE , FILTER1.LAST_GRP_CODE , FILTER1.LAST_DEPT_CODE
FROM ( SELECT EXTENT1.INV_REQ_ID , EXTENT1.INV_REQ_NO , EXTENT1.SO_ID , EXTENT1.INV_REQ_STUS_ID , EXTENT1.INV_REQ_PARTY_ID , EXTENT1.INV_REQ_REM , EXTENT1.INV_REQ_CRT_DT, EXTENT1.INV_REQ_CRT_USER_ID , EXTENT1.INV_REQ_UPD_DT , EXTENT1.INV_REQ_UPD_USER_ID , EXTENT1.INV_REQ_USER_ID_AUTO , EXTENT1.INV_REQ_HAS_ATTACH , EXTENT1.INV_REQ_ATTACH_FILE , EXTENT2.CODE CODE2 , EXTENT2.NAME INV_REQ_STUS_NAME, EXTENT3.CODE CODE3 , EXTENT3.CODE_DESC INV_REQ_PARTY_NAME, EXTENT4.USER_NAME INV_REQ_CRT_USER_NAME
, EXTENT5.LAST_ORG_CODE , EXTENT5.LAST_GRP_CODE , EXTENT5.LAST_DEPT_CODE
FROM SAL0050D EXTENT1 LEFT JOIN SYS0038M EXTENT2 ON EXTENT1.INV_REQ_STUS_ID = EXTENT2.STUS_CODE_ID LEFT JOIN SYS0013M EXTENT3 ON EXTENT1.INV_REQ_PARTY_ID = EXTENT3.CODE_ID LEFT JOIN SYS0047M EXTENT4 ON EXTENT1.INV_REQ_CRT_USER_ID = EXTENT4.USER_ID
LEFT JOIN (SELECT USR.USER_ID, C.ORG_CODE LAST_ORG_CODE, C.GRP_CODE LAST_GRP_CODE, C.DEPT_CODE LAST_DEPT_CODE FROM SYS0047M USR, ORG0005D A, ORG0001D B, ORG1001V C WHERE USR.USER_NAME = B.MEM_CODE AND B.MEM_ID = A.MEM_ID AND C.MEM_ID = B.MEM_ID AND USR.USER_STUS_ID = 1 AND B.STUS = 1 AND A.ORG_STUS_CODE_ID = 1
) EXTENT5 ON EXTENT1.INV_REQ_CRT_USER_ID = EXTENT5.USER_ID
WHERE 0 <> EXTENT1.INV_REQ_ID
AND EXTENT1.INV_REQ_STUS_ID IN
#{item}
AND EXTENT1.INV_REQ_STUS_ID IN ( 1,44,5 )
) FILTER1 JOIN SAL0001D EXTENT5 ON EXTENT5.SALES_ORD_ID = FILTER1.SO_ID WHERE 1=1
AND UPPER(FILTER1.INV_REQ_NO) LIKE '%' || UPPER(#{invReqNo}) || '%'
AND FILTER1.INV_REQ_CRT_DT >= TO_DATE(#{startCrtDt}||' 00:00:00', 'DD/MM/YYYY HH24:MI:SS')
AND FILTER1.INV_REQ_CRT_DT <= TO_DATE(#{endCrtDt} ||' 23:59:59', 'DD-MM-YYYY HH24:MI:SS')
AND EXTENT5.SALES_ORD_NO LIKE '%' || #{salesOrdNo} || '%'
AND FILTER1.INV_REQ_PARTY_ID = #{invReqPartyId}
AND UPPER(FILTER1.INV_REQ_CRT_USER_NAME) LIKE '%' || UPPER(#{invReqCrtUserName}) || '%'
AND FILTER1.LAST_ORG_CODE = #{orgCode}
AND FILTER1.LAST_GRP_CODE = #{grpCode}
AND FILTER1.LAST_DEPT_CODE = #{deptCode}

mapper.update:
        UPDATE SAL0050D
			 SET INV_REQ_STUS_ID = #{statusPop},
			       INV_REQ_UPD_DT = SYSDATE,
			       INV_REQ_UPD_USER_ID = #{userId},
			       INV_REQ_REJCT_RESN_ID = #{invReqStusParam}
		 WHERE INV_REQ_ID = #{gridInvReqId}

UPDATE SAL0050D
     SET INV_REQ_STUS_ID = #{statusPop},
           INV_REQ_UPD_DT = SYSDATE,
           INV_REQ_UPD_USER_ID = #{userId}
 WHERE INV_REQ_ID = #{gridInvReqId}

