mapper.select:
SELECT DISTINCT T.APP_TYPE_CODE , T.APP_TYPE_NAME , T.CRT_USER_ID , T.CUST_IC , T.CUST_NAME , T.CUST_VA_NO , T.DSC_BRNCH_ID , T.KEYIN_BRNCH_ID , T.ORD_ID , T.ORD_NO , T.ORD_STUS_CODE , T.PO_NO , T.PRODUCT_CODE , T.PRODUCT_NAME , T.REF_NO , T.RENT_STUS , T.SALESMAN_MEM_ID , T.SALESMAN_CODE , T.SALESMAN_MEM_TYPE_ID , T.BILLING_GRP_ID , T.MAIL_TEL_MOB , T.MAIL_TEL_FAX , T.MAIL_TEL_OFF , T.MAIL_TEL_RES , T.INST_TEL_FAX , T.INST_TEL_MOB , T.INST_TEL_OFF , T.INST_TEL_RES , T.SIRIM_NO , T.SERIAL_NO , T.PROMO_CODE , T.PROMO_DESC , T.RELATED_NO , T.RELATED_ID , 1 C1 , NVL(T.APP_TYPE_ID, 0) AS APP_TYPE_ID , NVL(T.CUST_ID, 0) AS CUST_ID , TO_CHAR(T.ORD_DT, 'DD/MM/YYYY') AS ORD_DT , NVL(T.ORD_STUS_ID, 0) AS ORD_STUS_ID , NVL(T.PRODUCT_ID, 0) AS PRODUCT_ID , T.PV_MONTH , T.PV_YEAR FROM ( SELECT /*+ use_nl( V1.som V1.ir V1.ie V1.ei V1.i V1.sod V1.cp3 V1.cp2 V1.rs V1.c V1.u V1.mem V1.cd V1.stk V1.pm V1.S ) */ V1.APP_TYPE_CODE , V1.APP_TYPE_ID , V1.APP_TYPE_NAME , V1.CRT_USER_ID , V1.CUST_IC , V1.CUST_NAME , V1.CUST_ID , V1.CUST_VA_NO , V1.DSC_BRNCH_ID , V1.KEYIN_BRNCH_ID , V1.ORD_DT , V1.ORD_ID , V1.ORD_NO , V1.ORD_STUS_CODE , V1.ORD_STUS_ID , V1.PO_NO , V1.PRODUCT_ID , V1.PRODUCT_CODE , V1.PRODUCT_NAME , V1.REF_NO , V1.RENT_STUS , V1.SALESMAN_MEM_ID , V1.SALESMAN_CODE , V1.SALESMAN_MEM_TYPE_ID , V1.BILLING_GRP_ID , V1.MAIL_TEL_MOB , V1.MAIL_TEL_FAX , V1.MAIL_TEL_OFF , V1.MAIL_TEL_RES , V1.INST_TEL_FAX , V1.INST_TEL_MOB , V1.INST_TEL_OFF , V1.INST_TEL_RES , V1.SIRIM_NO , V1.SERIAL_NO , V1.ITM_PRC_ID , V1.PV_MONTH , V1.PV_YEAR , V1.PROMO_CODE , V1.PROMO_DESC , V1.RELATED_NO , V1.RELATED_ID FROM SAL1013V V1 LEFT JOIN SAL1008V F1 ON V1.ORD_ID = F1.ORD_ID WHERE 1 = 1
AND V1.SALES_DT BETWEEN TO_DATE(#{ordStartDt}, 'YYYY-MM-DD') AND TO_DATE(#{ordEndDt}||' 23:59:59', 'YYYY-MM-DD HH24:MI:SS')
AND V1.SALES_DT BETWEEN TO_DATE(#{ordDt}, 'DD/MM/YYYY') AND TO_DATE(#{ordDt}||' 23:59:59', 'DD/MM/YYYY HH24:MI:SS')+1
AND V1.ORD_NO = #{ordNo}
AND V1.APP_TYPE_ID IN
#{item}
AND V1.ORD_STUS_ID IN
#{item}
AND V1.KEYIN_BRNCH_ID IN
#{item}
AND V1.DSC_BRNCH_ID IN
#{item}
AND V1.CUST_ID = #{custId}
AND UPPER(V1.CUST_NAME) LIKE '%'||UPPER(#{custName})||'%'
AND UPPER(V1.CUST_IC) LIKE '%'||UPPER(#{custIc})||'%'
AND V1.PRODUCT_ID = #{productId}
AND UPPER(V1.SALESMAN_CODE) = UPPER(#{salesmanCode})
AND V1.RENT_STUS IN
#{item}
AND UPPER(V1.REF_NO) = UPPER(#{refNo})
AND #{contactNo} IN (V1.MAIL_TEL_MOB, V1.MAIL_TEL_OFF, V1.MAIL_TEL_RES)
AND UPPER(V1.PROMO_CODE) = UPPER(#{promoCode})
AND UPPER(F1.ORG_CODE) = UPPER(#{orgCode})
AND UPPER(F1.GRP_CODE) = UPPER(#{grpCode})
AND UPPER(F1.DEPT_CODE) = UPPER(#{deptCode})
) T
ORDER BY T.ORD_NO ASC

SELECT DISTINCT V1.ORD_ID , V1.ORD_NO , V1.RENTAL_STUS , V1.ORD_STUS_ID , V1.ORD_STUS_CODE , V1.ORD_STUS_NAME , V1.ORD_DT , V1.INSTLMT_PRIOD , V1.ORD_AMT , V1.ORD_MTH_RENTAL , V1.ORD_PV , V1.ORD_PV_MONTH , V1.ORD_PV_YEAR , V1.ORD_REF_NO , V1.ORD_PO_NO , V1.ORD_DEPT_CODE , V1.ORD_GRP_CODE , V1.ORD_ORG_CODE , V1.ORD_CRT_USER_ID , V1.ORD_CRT_DT , V1.APP_TYPE_ID , V1.APP_TYPE_CODE , V1.APP_TYPE_DESC , V1.ITM_PRC_ID , V1.STOCK_ID , V1.STOCK_CODE , V1.STOCK_DESC , V1.MASTER_STK_ID , V1.CUST_ID , V1.CUST_TYPE , V1.CUST_NAME , V1.CUST_NRIC , V1.CUST_DOB , V1.CUST_NATION , V1.CUST_GENDER , V1.CUST_RACE , V1.CUST_EMAIL , V1.CUST_VA_NO , TO_CHAR(V1.CUST_PASSPORT_EXPR, 'DD/MM/YYYY') CUST_PASSPORT_EXPR , TO_CHAR(V1.CUST_VISA_EXPR, 'DD/MM/YYYY') CUST_VISA_EXPR , V1.ORD_PROMO_ID , V1.ORD_PROMO_CODE , V1.ORD_PROMO_DESC , V1.ORD_MEM_ID , V1.ORD_MEM_CODE , V1.ORD_MEM_NAME , V1.ORD_MEM_NRIC , V1.ORD_MEM_TYPE_ID , V1.ORD_MEM_TYPE_CODE , V1.ORD_MEM_TYPE_NAME , V1.COOL_OFF_PRIOD , V1.KEYIN_BRNCH_ID , V1.KEYIN_BRNCH_CODE , V1.KEYIN_BRNCH_NAME , V1.ORD_REM , V1.RENT_CHK_ID , V1.ORD_HM_ID , V1.ORD_SM_ID , V1.ORD_GM_ID , V1.ORD_ADDR_ID , V1.ORD_CNTC_ID , V1.CUST_CARE_CNT_ID , V1.ORD_PROMO_RELATED_NO , V1.UPD_DT , V1.UPD_USER_ID , V1.JOM_PAY_REF , V1.STK_CTGRY_ID , V1.STK_CTGRY_NAME , V1.CUST_TYPE_ID , V1.CUST_BILL_ID , V1.OBLIGT_YEAR , NVL(V1.EMP_CHK, 0) AS EMP_CHK , NVL(V1.EX_TRADE, 0) AS EX_TRADE , NVL(V1.GST_CHK, 0) AS GST_CHK , V1.CNVR_SCHEME_ID , V1.PROMO_DISC_PERIOD_TP , V1.PROMO_DISC_PERIOD_TP_NM , V1.PROMO_DISC_PERIOD , V1.NOR_AMT , V1.NOR_RNT_FEE , V1.DISC_RNT_FEE , V1.SRV_PAC_ID , V1.ADV_BILL , V1.CRT_DUR
, (SELECT SALES_ORD_NO FROM SAL0001D WHERE SALES_ORD_ID = V1.PCKAGE_BINDING_NO) AS PCKAGE_BINDING_ID , CASE WHEN V1.PCKAGE_BINDING_NO IS NOT NULL THEN V1.PCKAGE_BINDING_NO ELSE V7.SALES_ORD_ID END AS PCKAGE_BINDING_NO , V1.BNDL_ID , AUX.SALES_ORD_ID AUX_SALES_ORD_ID , AUX.SALES_ORD_NO AUX_SALES_ORD_NO , AUX.APP_TYPE_ID AUX_APP_TYPE , V1.CUST_TIN CUST_TIN , V1.SST_RGIST_NO SST_RGIST_NO FROM SAL1006V V1
LEFT JOIN SAL0001D V7 ON V7.PCKAGE_BINDING_NO = V1.ORD_ID LEFT JOIN HMC0011D HC ON HC.MAT_ORD_NO = V1.ORD_NO AND HC.ORD_SEQ_NO = V1.BNDL_ID LEFT JOIN SAL0001D AUX ON AUX.SALES_ORD_NO = HC.FRA_ORD_NO WHERE V1.ORD_ID = #{salesOrderId}

SELECT T.LOG_ID , T.SALES_ORD_ID , T.PRGRS_ID , T.LOG_DT , T.REF_ID , T.IS_LOK , T.LOG_CRT_USER_ID , T.LOG_CRT_DT , T.PRGRS , T.USER_NAME FROM ( SELECT T1.LOG_ID , T1.SALES_ORD_ID , T1.PRGRS_ID , T1.LOG_DT , T1.REF_ID , T1.IS_LOK , T1.LOG_CRT_USER_ID , T1.LOG_CRT_DT , T2.PRGRS , DECODE ( T3.USER_ID, NULL, '', T3.USER_NAME) AS USER_NAME FROM SAL0009D T1 JOIN SAL0010M T2 ON T2.PRGRS_ID = T1.PRGRS_ID LEFT JOIN SYS0047M T3 ON T3.USER_ID = T1.LOG_CRT_USER_ID WHERE T1.SALES_ORD_ID = #{salesOrderId}
AND T2.PRGRS_ID = #{prgrsId}
ORDER BY T1.LOG_ID DESC ) T WHERE ROWNUM <= 1

SELECT
RQST.RQST_ID , SOM.SALES_ORD_ID SALES_ORD_ID , SOM.SALES_ORD_NO SALES_ORD_NO , RQST.STUS_CODE_ID STUS_CODE_ID , STUS_CODE.NAME STUS , APP_TYPE.CODE_NAME APP_TYPE , CUST.NAME CUST_NAME , CUST.NRIC NRIC , INST_STUS.NAME INST_STUS , RQST.RQST_TYPE_ID TYPE_ID , RQST_TYPE.CODE_NAME RQST_TYPE , RQST.RQST_REM RQST_REM , RQST.REM REM , CASE WHEN RQST.RQST_TYPE_ID = 5970 AND (BNDL_ID IS NULL OR BNDL_ID = 0) THEN TO_BRNCH1.NAME WHEN RQST.RQST_TYPE_ID = 5970 AND BNDL_ID > 0 THEN TO_BRNCH2.NAME ELSE BRNCH.NAME END DSC , CRT.USER_NAME CRT_USER , TO_CHAR(RQST.CRT_DT,'DD/MM/YYYY HH24:MI:SS') CRT_DT , UPD.USER_NAME UPD_USER , TO_CHAR(RQST.UPD_DT,'DD/MM/YYYY HH24:MI:SS') UPD_DT , RQST.RQST_DATA_FR , RQST.RQST_DATA_TO , ORG.ORG_CODE , ORG.GRP_CODE , ORG.DEPT_CODE
FROM SAL0269D RQST JOIN SAL0001D SOM ON SOM.SALES_ORD_ID = RQST.SALES_ORD_ID JOIN SAL0045D INST ON INST.SALES_ORD_ID = SOM.SALES_ORD_ID JOIN SYS0013M RQST_TYPE ON RQST_TYPE.CODE_ID = RQST.RQST_TYPE_ID JOIN SYS0013M APP_TYPE ON APP_TYPE.CODE_ID = SOM.APP_TYPE_ID JOIN SYS0038M STUS_CODE ON STUS_CODE.STUS_CODE_ID = RQST.STUS_CODE_ID JOIN SAL0029D CUST ON CUST.CUST_ID = SOM.CUST_ID JOIN SYS0047M CRT ON CRT.USER_ID = RQST.CRT_USER_ID JOIN SYS0047M UPD ON UPD.USER_ID = RQST.UPD_USER_ID LEFT JOIN SYS0005M BRNCH ON BRNCH.BRNCH_ID = INST.BRNCH_ID LEFT JOIN SAL0023D TO_ADDR ON TO_ADDR.CUST_ADD_ID = RQST.RQST_DATA_TO LEFT JOIN SYS0064M TO_AREA ON TO_AREA.AREA_ID = TO_ADDR.AREA_ID LEFT JOIN SYS0005M TO_BRNCH1 ON TO_BRNCH1.CODE = TO_AREA.CT_BRNCH_CODE AND (BNDL_ID IS NULL OR BNDL_ID = 0) LEFT JOIN SYS0005M TO_BRNCH2 ON TO_BRNCH2.CODE = TO_AREA.DT_BRNCH_CODE AND (BNDL_ID > 0) JOIN SYS0038M INST_STUS ON INST_STUS.STUS_CODE_ID = (CASE WHEN SOM.STUS_CODE_ID = 1 THEN 24 ELSE 25 END) LEFT JOIN ORG1001V ORG ON ORG.MEM_CODE = CRT.USER_NAME
WHERE 1=1
AND RQST.RQST_ID = #{rqstId}
AND SOM.SALES_ORD_NO = #{ordNo}
AND SOM.SALES_ORD_ID = #{salesOrdId}
AND RQST.CRT_DT >= TO_DATE(#{startCrtDt},'DD/MM/YYYY')
AND RQST.CRT_DT <= TO_DATE(#{endCrtDt},'DD/MM/YYYY')+1
AND UPPER(CRT.USER_NAME) LIKE UPPER('%' || #{crtUserId} || '%')
AND CUST.CUST_ID = #{custId}
AND UPPER(CUST.NAME) LIKE UPPER('%' || #{custName} || '%')
AND UPPER(CUST.NRIC) LIKE UPPER('%' || #{custIc} || '%')
AND SOM.APP_TYPE_ID IN
#{item}
AND INST_STUS.STUS_CODE_ID IN
#{item}
AND (
( RQST.RQST_TYPE_ID = 5969 AND INST.BRNCH_ID IN
#{item}
) OR ( RQST.RQST_TYPE_ID = 5970 AND TO_BRNCH1.BRNCH_ID IN
#{item}
) OR ( RQST.RQST_TYPE_ID = 5970 AND TO_BRNCH2.BRNCH_ID IN
#{item}
) )
AND RQST.STUS_CODE_ID IN
#{item}
AND RQST.RQST_TYPE_ID = #{typeId}
AND UPPER(ORG.ORG_CODE) = UPPER(#{orgCode})
AND UPPER(ORG.GRP_CODE) = UPPER(#{grpCode})
AND UPPER(ORG.DEPT_CODE) = UPPER(#{deptCode})
AND SOM.BNDL_ID > 0
AND (SOM.BNDL_ID = 0 OR SOM.BNDL_ID IS NULL)
ORDER BY RQST_ID DESC

mapper.update:
UPDATE SAL0269D
SET STUS_CODE_ID = #{reqStusId},
      REM = #{rem},
      UPD_DT = SYSDATE,
      UPD_USER_ID = #{updator}
WHERE RQST_ID = #{rqstId}

UPDATE SAL0001D
SET CUST_ADD_ID = #{custAddrId},
    UPD_DT =  SYSDATE,
    UPD_USER_ID = #{updator}
WHERE SALES_ORD_ID = #{salesOrdId}

UPDATE SAL0001D
SET CUST_CNT_ID = #{custCntcId},
    UPD_DT = SYSDATE,
    UPD_USER_ID = #{updator}
WHERE SALES_ORD_ID = #{salesOrdId}
