)
SELECT DISTINCT A.SALES_ORD_NO , A.SALES_ORD_ID , F.CCP_ID AS EDIT_CCP_ID , D.ROT_ID AS CCP_ROT_ID , D.ROT_ORD_ID AS EDIT_ORD_ID , G.CODE AS EDIT_APP_TYPE_CODE , F.CCP_ID AS SAVE_CCP_ID , D.ROT_ORD_ID SAVE_ORD_ID FROM SAL0001D A JOIN HMC0011D B ON A.SALES_ORD_NO IN (B.MAT_ORD_NO, B.FRA_ORD_NO) LEFT JOIN BNDL_TBL C ON 1=1 JOIN SAL0275M D ON A.SALES_ORD_NO = D.ROT_ORD_NO AND D.ROT_STUS =1 JOIN SAL0276D E ON D.ROT_ID =E.ROT_ID JOIN SAL0277D F ON E.ROT_ID =F.ROT_ID JOIN SYS0013M G ON G.CODE_ID = E.ROT_APP_TYPE WHERE C.SALES_ORD_NO IN (B.MAT_ORD_NO , B.FRA_ORD_NO) AND A.SALES_ORD_NO <> C.SALES_ORD_NO AND ROWNUM = 1

WITH GRP_TBL AS (
SELECT ROT_ORD_NO , NVL(GROUP_ID,0) GROUP_ID FROM SAL0275M where rot_id = #{rotID}
SELECT ROT_ORD_NO , NVL(GROUP_ID,0) GROUP_ID FROM SAL0275M where rot_id = #{ccpRotId}
)
SELECT A.ROT_ID , A.ROT_ORD_ID , C.CCP_ID , E.CODE AS APP_TYPE_CODE FROM SAL0275M A JOIN GRP_TBL B ON A.GROUP_ID = B.GROUP_ID JOIN SAL0277D C ON A.ROT_ID = C.ROT_ID JOIN SAL0276D D ON A.ROT_ID = D.ROT_ID JOIN SYS0013M E ON E.CODE_ID = D.ROT_APP_TYPE WHERE A.ROT_ORD_NO <> B.ROT_ORD_NO AND B.GROUP_ID <> 0
