mapper.update:
    UPDATE SAL0409M
    SET
        PORTAL_NAME        = #{portalName}
        ,PORTAL_STUS        = #{portalStus}
        ,CORP_TYPE          = #{corpType}
        ,INV_METHOD         = #{invMethod}
        ,LOGIN_ID           = #{loginId}
        ,PORTAL_PASS        = #{portalPass}
        ,PORTAL_URL         = #{portalUrl}
        ,PAYMENT_TERM       = #{paymentTerm}
        ,REG_PERIOD_MONTH   = #{regMonth}
        ,REG_PERIOD_YEAR    = #{regYear}
        ,REMARK             = #{remark}
        ,RENEWAL_PERIOD     = #{renewPeriod}
        ,RENEWAL_FEE        = #{renewFee}
        ,CCD_PIC1           = #{ccdPic1}
        ,CCD_PIC2           = #{ccdPic2}
        ,CCD_PIC3           = #{ccdPic3}

        ,UPD_USER_ID        = #{updator}
        ,UPD_DT             = SYSDATE
    WHERE PORTAL_ID = #{portalId}

mapper.select:
SELECT A.PORTAL_ID ,A.PORTAL_CODE ,A.PORTAL_NAME ,A.PORTAL_STUS ,A.CORP_TYPE ,A.INV_METHOD ,A.LOGIN_ID ,A.PORTAL_PASS ,A.PORTAL_URL ,A.PAYMENT_TERM ,A.REG_PERIOD_MONTH ,A.REG_PERIOD_YEAR ,A.REMARK ,A.RENEWAL_PERIOD ,A.RENEWAL_FEE ,A.CCD_PIC1 ,A.CCD_PIC2 ,A.CCD_PIC3 ,B.ATCH_FILE_GRP_ID ,C.ATCH_FILE_ID ,A.CRT_USER_ID ,A.CRT_DT ,A.UPD_USER_ID ,A.UPD_DT
FROM SAL0409M A LEFT JOIN SYS0070M B ON B.ATCH_FILE_GRP_ID = A.ATCH_FILE_GRP_ID LEFT JOIN SYS0071D C ON C.ATCH_FILE_ID = B.ATCH_FILE_ID WHERE 1=1
AND A.PORTAL_ID = #{portalId}

mapper.update:
