mapper.update:
			UPDATE SAMPLE
			SET ID=#{id}
				, NAME=#{name}
				, DESCRIPTION=#{description}
				, USE_YN=#{useYn}
				  WHERE ID=#{id}

mapper.select:
SELECT 'ID' as ID, 'NAME' as NAME, 'DESCRIPTION' as DESCRIPTION, 'USE_YN' as USE_YN, 'REG_USER' as REG_USER FROM DUAL WHERE 1=1
AND #{sId} = #{sId}
AND #{sName} = #{sName}
AND ID LIKE '%' || #{searchKeyword} || '%'
AND NAME LIKE '%' || #{searchKeyword} || '%'
UNION ALL SELECT 'ID2' as ID, 'NAME2' as NAME, 'DESCRIPTION2' as DESCRIPTION, 'USE_YN2' as USE_YN, 'REG_USER2' as REG_USER FROM DUAL
